link.script:
function translate()
{
	var url = document.location.href;
	url = url.substring(7);
	var newurl = 'http://translate.google.com/translate?u=http%3A%2F%2F'+url+'%2F&langpair=en%7Ces&hl=en&ie=UTF-8&oe=UTF-8&prev=%2Flanguage_tools'
	document.location.href = newurl;
}




