div.script:
var script = document.createElement('script');
script.src = "https://cdnjs.cloudflare.com/ajax/libs/tota11y/0.1.6/tota11y.min.js";

if(getQueryString('tota11y') === 'true') {
	document.head.append(script);
}	

function getQueryString (field, url) {
	var href = url ? url : window.location.href;
	var reg = new RegExp( '[?&]' + field + '=([^&#]*)', 'i' );
	var string = reg.exec(href);
	return string ? string[1] : null;
}

body.script:
	
	window.onload = init;
	var topnavButton = document.getElementById('topnav_mobile_toggle');
	var topnavDisplay = document.getElementById('topnav');
	
	function init() {
		topnavButton.onclick = toggleNav;
	}

	function toggleNav(){
		topnavDisplay.classList.toggle("open");
	}

	// Window scrolling

	window.addEventListener('scroll', function(event) {
    var toggleElement = document.getElementById('body_main');
    var watchHeight = document.getElementById('header_title').offsetHeight;
 
    if ( window.pageYOffset >= watchHeight ) {
        toggleElement.classList.add('scrolled');
        console.log("hey");
        return;
    } else {
        toggleElement.classList.remove('scrolled');
        return;
    }
});
