link.script:
  var _gaq = _gaq || [];
  _gaq.push(['_setAccount', 'UA-10370260-1']);
  _gaq.push(['_trackPageview']);

  (function() {
    var ga = document.createElement('script'); ga.type = 'text/javascript'; ga.async = true;
    ga.src = ('https:' == document.location.protocol ? 'https://ssl' : 'http://www') + '.google-analytics.com/ga.js';
    var s = document.getElementsByTagName('script')[0]; s.parentNode.insertBefore(ga, s);
  })();

div.script:
		/* Tabs */
		j = jQuery.noConflict();
		j(document).ready(function () {
			j('#tabzine .tabnav a').each(function () {
				var target = j(this).attr('href');
				if (!j('' + target + '.tabdiv').hasClass('active')) {
					j('' + target + '.tabdiv').hide();
				}
			});
			j('#tabzine .tabnav a').click(function (e) {
				e.preventDefault();
				var target = j(this).attr('href');
				console.log(target);
				j('#tabzine .tabnav a').removeClass('active');
				j(this).addClass('active');
				j('.tabdiv').removeClass('active');
				j('' + target + '.tabdiv').addClass('active');
				j('.tabdiv').hide();
				j(target).show();
				j(target).addClass('active');
			});

			if(j('#tweets').length > 0){
				var config = {
				  "id": '476766195832483841',
				  "domId": 'tweets',
				  "maxTweets": 3,
				  "enableLinks": true,
				  "showImages": false,
				  "showRetweet": false
				};
				twitterFetcher.fetch(config);
			}

			var height = 0;
			j('.behind-section-widget .outsidelink').each(function(){
				if(j(this).height() > height){
					height = j(this).height();
				}
			});
			j('.behind-section-widget .outsidelink').css({'height':height});

			j('#slider').flexslider({
				animation: "slide",
				controlNav: false,
				directionNav: false,
				slideshowSpeed: 5000,
				after: function(slider){
					j('.home .portfolio-links li').removeClass('active');
					j('.home .portfolio-links li:eq('+slider.currentSlide+')').addClass('active');
				}
			});
			if(j('.home .portfolio-links').length > 0){
				j('.home .portfolio-links li').click(function(){
					var slider = j('#slider').data('flexslider');
					slider.flexAnimate(j(this).index());
					slider.pause();
					setTimeout(function(){ slider.play(); }, 5000);
					j('.home .portfolio-links li').removeClass('active');
					j(this).addClass('active');
				});
			}

			if(j('.bookmarks').length > 0){
				j('.bookmarks .blogroll li').each(function(){
					console.log(j(this).children('a').text());
					j(this).children('a').attr('target','_blank');
				});
			}
		});

		jQuery(window).load(function(){
			if(jQuery(window).width() > 1005){
				if(jQuery('.bookmarks').length > 0){
					var height = 0;
					jQuery('.behind-section-widget .ednextlink').each(function(){
						if(jQuery(this).height() > height){
							height = jQuery(this).height();
						}
					});
					jQuery('.behind-section-widget .ednextlink').css({'height':height});
				}
			}
		});
