link.script:
        $(document).ready(function () {
            SetCookie('SWScreenWidth', screen.width);
            SetCookie('SWClientWidth', document.body.clientWidth);
            
            $("div.ui-article:last").addClass("last-article");
            $("div.region .app:last").addClass("last-app");

            // get on screen alerts
            var isAnyActiveOSA = 'False';
            var onscreenAlertCookie = GetCookie('Alerts');

            if (onscreenAlertCookie == '' || onscreenAlertCookie == undefined) {
                onscreenAlertCookie = "";
            }
            if (isAnyActiveOSA == 'True') {
                GetContent(homeURL + "/cms/Tools/OnScreenAlerts/UserControls/OnScreenAlertDialogListWrapper.aspx?OnScreenAlertCookie=" + onscreenAlertCookie + "&SiteID=1", "onscreenalert-holder", 2, "OnScreenAlertCheckListItem();");
            }

            

        });

    // ADA SKIP NAV
    $(document).ready(function () {
        $(document).on('focus', '#skipLink', function () {
            $("div.sw-skipnav-outerbar").animate({
                marginTop: "0px"
            }, 500);
        });

        $(document).on('blur', '#skipLink', function () {
            $("div.sw-skipnav-outerbar").animate({
                marginTop: "-30px"
            }, 500);
        });
    });

    // ADA MYSTART
    $(document).ready(function () {
        var top_level_nav = $('.sw-mystart-nav');

        // Set tabIndex to -1 so that top_level_links can't receive focus until menu is open
        // school dropdown
        $(top_level_nav).find('ul').find('a').attr('tabIndex', -1);

        // my account dropdown
        $(top_level_nav).next('ul').find('a').attr('tabIndex', -1);

        var openNavCallback = function(e, element) {
             // hide open menus
            hideMyStartBarMenu();

            // show school dropdown
            if ($(element).find('ul').length > 0) {
                $(element).find('.sw-dropdown').css('display', 'block').attr('aria-hidden', 'false');
                $(element).find('.sw-dropdown').find('li:first-child a').focus()
            }

            // show my account dropdown
            if ($(element).next('ul').length > 0) {
                $(element).next('.sw-dropdown').css('display', 'block').attr('aria-hidden', 'false');
                $(element).next('.sw-dropdown').find('li:first-child a').focus();
                $('#sw-mystart-account').addClass("clicked-state");
            }
        }

        $(top_level_nav).click(function (e) {
            openNavCallback(e, this);
        });

        $('.sw-dropdown-list li').click(function(e) {
            e.stopImmediatePropagation();
            $(this).focus();
        });
        
        // Bind arrow keys for navigation
        $(top_level_nav).keydown(function (e) {
            if (e.keyCode == 37) { //key left
                e.preventDefault();

                // This is the first item
                if ($(this).prev('.sw-mystart-nav').length == 0) {
                    $(this).parents('div').find('.sw-mystart-nav').last().focus();
                } else {
                    $(this).prev('.sw-mystart-nav').focus();
                }
            } else if (e.keyCode == 38) { //key up
                e.preventDefault();

                // show school dropdown
                if ($(this).find('ul').length > 0) {
                    $(this).find('div.sw-dropdown').css('display', 'block').find('ul').attr('aria-hidden', 'false').find('a').attr('tabIndex', 0).last().focus();
                }

                // show my account dropdown
                if ($(this).find('ul').length > 0) {
                    $(this).find('ul.sw-dropdown').css('display', 'block').attr('aria-hidden', 'false').find('a').attr('tabIndex', 0).last().focus();
                }
            } else if (e.keyCode == 39) { //key right
                e.preventDefault();

                // This is the last item
                if ($(this).next('.sw-mystart-nav').length == 0) {
                    $(this).parents('div').find('.sw-mystart-nav').first().focus();
                } else {
                    $(this).next('.sw-mystart-nav').focus();
                }
            } else if (e.keyCode == 40) { //key down
                e.preventDefault();

                // show school dropdown
                if ($(this).find('ul').length > 0) {
                    $(this).find('div.sw-dropdown').css('display', 'block').find('ul').attr('aria-hidden', 'false').find('a').attr('tabIndex', 0).first().focus();
                }

                // show my account dropdown
                if ($(this).next('ul').length > 0) {
                    $(this).next('ul.sw-dropdown').css('display', 'block').attr('aria-hidden', 'false').find('a').attr('tabIndex', 0).first().focus();
                }
            } else if (e.keyCode == 13 || e.keyCode == 32) { //enter key
                // If submenu is hidden, open it
                e.preventDefault();

                
                openNavCallback(e, this);
                $(this).parent('li').find('ul[aria-hidden=true]').attr('aria-hidden', 'false').find('a').attr('tabIndex', 0).first().focus();
            } else if (e.keyCode == 27) { //escape key
                e.preventDefault();
                hideMyStartBarMenu();
            } else {
                $(this).parent('.sw-mystart-nav').find('ul[aria-hidden=false] a').each(function () {
                    if (typeof keyCodeMap != "undefined" && $(this).text().substring(0, 1).toLowerCase() == keyCodeMap[e.keyCode]) {
                        $(this).focus();
                        return false;
                    }
                });
            }
        });

        // school dropdown
        var startbarlinks = $(top_level_nav).find('ul').find('a');
        bindMyStartBarLinks(startbarlinks);

        // my account dropdown
        var myaccountlinks = $(top_level_nav).next('ul').find('a');
        bindMyStartBarLinks(myaccountlinks);

        function bindMyStartBarLinks(links) {
            $(links).keydown(function (e) {
                e.stopPropagation();

                if (e.keyCode == 38) { //key up
                    e.preventDefault();

                    // This is the first item
                    if ($(this).parent('li').prev('li').length == 0) {
                        if ($(this).parents('ul').parents('.sw-mystart-nav').length > 0) {
                            $(this).parents('ul').parents('.sw-mystart-nav').focus();
                        } else {
                            $(this).parents('ul').prev('.sw-mystart-nav').focus();
                        }
                    } else {
                        $(this).parent('li').prev('li').find('a').first().focus();
                    }
                } else if (e.keyCode == 40) { //key down
                    e.preventDefault();

                    if ($(this).parent('li').next('li').length == 0) {
                        if ($(this).parents('ul').parents('.sw-mystart-nav').length > 0) {
                            $(this).parents('ul').parents('.sw-mystart-nav').focus();
                        } else {
                            $(this).parents('ul').prev('.sw-mystart-nav').focus();
                        }
                    } else {
                        $(this).parent('li').next('li').find('a').first().attr('tabIndex', 0);
                        $(this).parent('li').next('li').find('a').first().focus();
                    }
                } else if (e.keyCode == 27 || e.keyCode == 37) { // escape key or key left
                    e.preventDefault();
                    hideMyStartBarMenu();
                } else if (e.keyCode == 32) { //enter key
                    e.preventDefault();
                    window.location = $(this).attr('href');
                } else {
                    var found = false;

                    $(this).parent('div').nextAll('li').find('a').each(function () {
                        if (typeof keyCodeMap != "undefined" && $(this).text().substring(0, 1).toLowerCase() == keyCodeMap[e.keyCode]) {
                            $(this).focus();
                            found = true;
                            return false;
                        }
                    });

                    if (!found) {
                        $(this).parent('div').prevAll('li').find('a').each(function () {
                            if (typeof keyCodeMap != "undefined" && $(this).text().substring(0, 1).toLowerCase() == keyCodeMap[e.keyCode]) {
                                $(this).focus();
                                return false;
                            }
                        });
                    }
                }
            });
        }
        
        // Hide menu if click or focus occurs outside of navigation
        $('#sw-mystart-inner').find('.sw-mystart-nav').last().keydown(function (e) {
            if (e.keyCode == 9) {
                // If the user tabs out of the navigation hide all menus
                hideMyStartBarMenu();
            }
        });

        /*$(document).click(function() { 
            hideMyStartBarMenu();
        });*/

        // try to capture as many custom MyStart bars as possible
        $('.sw-mystart-button').find('a').focus(function () {
            hideMyStartBarMenu();
        });

        $('#sw-mystart-inner').click(function (e) {
            e.stopPropagation();
        });

        $('ul.sw-dropdown-list').blur(function () {
            hideMyStartBarMenu();
        });

        $('#ui-btn-mypasskey').focus(function () {
            hideMyStartBarMenu();
        });

        $('#ui-btn-sitemanager').focus(function () {
            hideMyStartBarMenu();
        });

        $('#ui-btn-myview').focus(function () {
            hideMyStartBarMenu();
        });

        $('#ui-btn-signin').focus(function () {
            hideMyStartBarMenu();
        });

        $('#ui-btn-register').focus(function () {
            hideMyStartBarMenu();
        });

        // button click events
        $('div.sw-mystart-button.home a').keydown(function (e) {
            e.stopImmediatePropagation();

            if (e.keyCode == 13) {
                $(this).click();
            }
        });

        $('div.sw-mystart-button.pw a').keydown(function (e) {
            e.stopImmediatePropagation();

            if (e.keyCode == 13) {
                $(this).click();
            }
        });

        $('div.sw-mystart-button.manage a').keydown(function (e) {
            e.stopImmediatePropagation();

            if (e.keyCode == 13) {
                $(this).click();
            }
        });

        $('#sw-mystart-account').keydown(function (e) {
            e.stopImmediatePropagation();

            if (e.keyCode == 13) {
                $(this).addClass('clicked-state');
                $('#sw-myaccount-list').show();
            }
        });

        $('#sw-mystart-mypasskey a').keydown(function (e) {
            e.stopImmediatePropagation();

            if (e.keyCode == 13) {
                $(this).click();
            }
        });

        $('div.sw-mystart-button.signin a').keydown(function (e) {
            e.stopImmediatePropagation();

            if (e.keyCode == 13) {
                $(this).click();
            }
        });

        $('div.sw-mystart-button.register a').keydown(function (e) {
            e.stopImmediatePropagation();

            if (e.keyCode == 13) {
                $(this).click();
            }
        });
    });

    function hideMyStartBarMenu() {
        $('.sw-dropdown').attr('aria-hidden', 'true').css('display', 'none');
        $('#sw-mystart-account').removeClass("clicked-state");
    }

    // ADA CHANNEL NAV
    $(document).ready(function() {
        var channelCount;
        var channelIndex = 1;
        var settings = {
            menuHoverClass: 'hover'
        };

        // Add ARIA roles to menubar and menu items
        $('[id="channel-navigation"]').attr('role', 'menubar').find('li a').attr('role', 'menuitem').attr('tabindex', '0');

        var top_level_links = $('[id="channel-navigation"]').find('> li > a');
        channelCount = $(top_level_links).length;


        $(top_level_links).each(function() {
            $(this).attr('aria-posinset', channelIndex).attr('aria-setsize', channelCount);
            $(this).next('ul').attr({ 'aria-hidden': 'true', 'role': 'menu' });

            if ($(this).parent('li.sw-channel-item').children('ul').length > 0) {
                $(this).attr('aria-haspopup', 'true');
            }

            var sectionCount = $(this).next('ul').find('a').length;
            var sectionIndex = 1;
            $(this).next('ul').find('a').each(function() {
                $(this).attr('tabIndex', -1).attr('aria-posinset', sectionIndex).attr('aria-setsize', sectionCount);
                sectionIndex++;
            });
            channelIndex++;

        });

        $(top_level_links).focus(function () {
            //hide open menus
            hideChannelMenu();

            if ($(this).parent('li').find('ul').length > 0) {
                $(this).parent('li').addClass(settings.menuHoverClass).find('ul').attr('aria-hidden', 'false').css('display', 'block');
            }
        });

        // Bind arrow keys for navigation
        $(top_level_links).keydown(function (e) {
            if (e.keyCode == 37) { //key left
                e.preventDefault();

                // This is the first item
                if ($(this).parent('li').prev('li').length == 0) {
                    $(this).parents('ul').find('> li').last().find('a').first().focus();
                } else {
                    $(this).parent('li').prev('li').find('a').first().focus();
                }
            } else if (e.keyCode == 38) { //key up
                e.preventDefault();

                if ($(this).parent('li').find('ul').length > 0) {
                    $(this).parent('li').addClass(settings.menuHoverClass).find('ul').css('display', 'block').attr('aria-hidden', 'false').find('a').attr('tabIndex', 0).last().focus();
                }
            } else if (e.keyCode == 39) { //key right
                e.preventDefault();

                // This is the last item
                if ($(this).parent('li').next('li').length == 0) {
                    $(this).parents('ul').find('> li').first().find('a').first().focus();
                } else {
                    $(this).parent('li').next('li').find('a').first().focus();
                }
            } else if (e.keyCode == 40) { //key down
                e.preventDefault();

                if ($(this).parent('li').find('ul').length > 0) {
                    $(this).parent('li')
                         .addClass(settings.menuHoverClass)
                         .find('ul.sw-channel-dropdown').css('display', 'block')
                         .attr('aria-hidden', 'false')
                         .find('a').attr('tabIndex', 0)
                         .first().focus();
                }
            } else if (e.keyCode == 13 || e.keyCode == 32) { //enter key
                // If submenu is hidden, open it
                e.preventDefault();

                $(this).parent('li').find('ul[aria-hidden=true]').attr('aria-hidden', 'false').addClass(settings.menuHoverClass).find('a').attr('tabIndex', 0).first().focus();
            } else if (e.keyCode == 27) { //escape key
                e.preventDefault();
                hideChannelMenu();
            } else {
                $(this).parent('li').find('ul[aria-hidden=false] a').each(function () {
                    if (typeof keyCodeMap != "undefined" && $(this).text().substring(0, 1).toLowerCase() == keyCodeMap[e.keyCode]) {
                        $(this).focus();
                        return false;
                    }
                });
            }
        });

        var links = $(top_level_links).parent('li').find('ul').find('a');

        $(links).keydown(function (e) {
            if (e.keyCode == 38) {
                e.preventDefault();

                // This is the first item
                if ($(this).parent('li').prev('li').length == 0) {
                    $(this).parents('ul').parents('li').find('a').first().focus();
                } else {
                    $(this).parent('li').prev('li').find('a').first().focus();
                }
            } else if (e.keyCode == 40) {
                e.preventDefault();

                if ($(this).parent('li').next('li').length == 0) {
                    $(this).parents('ul').parents('li').find('a').first().focus();
                } else {
                    $(this).parent('li').next('li').find('a').first().focus();
                }
            } else if (e.keyCode == 27 || e.keyCode == 37) {
                e.preventDefault();
                $(this).parents('ul').first().prev('a').focus().parents('ul').first().find('.' + settings.menuHoverClass).removeClass(settings.menuHoverClass);
            } else if (e.keyCode == 32) {
                e.preventDefault();
                window.location = $(this).attr('href');
            } else {
                var found = false;

                $(this).parent('li').nextAll('li').find('a').each(function () {
                    if (typeof keyCodeMap != "undefined" && $(this).text().substring(0, 1).toLowerCase() == keyCodeMap[e.keyCode]) {
                        $(this).focus();
                        found = true;
                        return false;
                    }
                });

                if (!found) {
                    $(this).parent('li').prevAll('li').find('a').each(function () {
                        if (typeof keyCodeMap != "undefined" && $(this).text().substring(0, 1).toLowerCase() == keyCodeMap[e.keyCode]) {
                            $(this).focus();
                            return false;
                        }
                    });
                }
            }
        });

        function hideChannelMenu() {
            $('li.sw-channel-item.' + settings.menuHoverClass).removeClass(settings.menuHoverClass).find('ul').attr('aria-hidden', 'true').css('display', 'none').find('a').attr('tabIndex', -1);
        }
        
        // Hide menu if click or focus occurs outside of navigation
        $('[id="channel-navigation"]').find('a').last().keydown(function (e) {
            if (e.keyCode == 9) {
                // If the user tabs out of the navigation hide all menus
                hideChannelMenu();
            }
        });

        $('[id="channel-navigation"]').find('a').first().keydown(function (e) {
            if (e.keyCode == 9) {
                // hide open MyStart Bar menus
                hideMyStartBarMenu();
            }
        });

        /*$(document).click(function() {
            hideChannelMenu();
        });*/

        $('[id="channel-navigation"]').click(function (e) {
            e.stopPropagation();
        });
    });

    $(document).ready(function() {
        $('input.required').each(function() {
            if ($('label[for="' + $(this).attr('id') + '"]').length > 0) {
                if ($('label[for="' + $(this).attr('id') + '"]').html().indexOf('recStar') < 0) {
                    $('label[for="' + $(this).attr('id') + '"]').prepend('* ');
                }
            }
        });

        $(document).ajaxComplete(function() {
            $('input.required').each(function() {
                if ($('label[for="' + $(this).attr('id') + '"]').length > 0) {
                    if ($('label[for="' + $(this).attr('id') + '"]').html().indexOf('recStar') < 0) {
                        $('label[for="' + $(this).attr('id') + '"]').prepend('* ');
                    }
                }
            });
        });
    });

meta.script:
//CANVAS CURVE
var LTIE9;

function drawCurve() {
	var canvas = document.getElementById("curve-overlay");
	//IF IE
	if (typeof(G_vmlCanvasManager) != "undefined"){G_vmlCanvasManager.initElement(canvas);}
	var ctx = canvas.getContext("2d");

	draw(ctx);
}

function draw(ctx) {
	// layer1/Path
	ctx.save();
	ctx.beginPath();
	ctx.moveTo(0.0, 291.0);
	ctx.bezierCurveTo(0.0, 291.0, 116.0, 280.0, 146.0, 148.0);
	ctx.bezierCurveTo(146.0, 148.0, 164.0, 84.0, 158.0, 0.0);
	ctx.lineTo(163.0, 0.0);
	ctx.lineTo(163.0, 291.0);
	ctx.lineTo(0.0, 291.0);
	ctx.closePath();
	ctx.fillStyle='#4D0000';
	ctx.fill();
	ctx.restore();
}

link.script:
var csKeyCodes = { "tab": 9, "enter": 13, "esc": 27, "space": 32, "end": 35, "home": 36, "left": 37, "up": 38, "right": 39, "down": 40 };
$(window).load(function() {
	searchText();

    //ACCESSIBLE NAV APP
    $(".ui-widget.app.navigation").csAccessibleNavigationApp();
    $("#sw-mystart-outer").remove();
});
$(function(){
	photoGallery();
	useChannelForSection();
	dynamicContent();
	drawCurve();
	iconSwitch();
	footerSwitch();
});
function iconSwitch() {
	var facebookToggle = 'false';
	var twitterToggle = 'false';

	if(facebookToggle == "true" && twitterToggle == "true") {
		$("#social-icons").show();
	} else if(facebookToggle == "false" && twitterToggle == "true") {
		$("#social-icons").show();
		$("#social-icons .facebook").hide();
		$("#social-icons .twitter").css("margin-left","8px").show();
	} else if(twitterToggle == "false" && facebookToggle == "true") {
		$("#social-icons").show();
		$("#social-icons .twitter").hide();
		$("#social-icons icon-font.facebook").show();
	}
}
function footerSwitch() {
	var advancedToggle = 'true';
	if(advancedToggle == "true") {
		$("#advanced-logo").removeClass("hidden");
	}
}

function photoGallery(){
	//DOC #sw-content-container10.region.ui-hp -- IF .gallery-container NOT PRESENT THEN ADD .creativeGalleryRotate
	if(!$("#sw-content-container10.region.ui-hp .gallery-container").length){
		// PHOTO GALLERY CONFIGURATION
		$("#sw-content-container10.ui-hp div.ui-widget.app.gallery").creativeGalleryRotate({
			"imageHeight"		: 304,
			"imageControls"		: "on",
			"animateType"		: "fade", // CHOICES - "slide"
			"animateSpeed"		: 3,
			"pauseHover"		: ['all'], // CHOICES - ['title'], ['overlay'], ['caption'], ['all']
			"autoRotate"		: "on",
			"useTitles"		: "off",
			"useCaptions"		: "on",
			"useOverlay"		: "on",
			"randomOrder"		: "off",
			"linkedElement"		: ['picture'], // CHOICES - ['picture'], ['title'], ['overlay']
			"itemizedBullets"	: "off",
			"thumbnails"		: "off",
			"thumbnailWidth"	: 50,
			"thumbnailHeight"	: 32,
			"thumbnailControls"	: "off",
			"thumbnailLimit"	: 3,
			"addClass"		: "unclassed",
			"moreLinkText"		: "off" 
		});
	}
    $(".image-button.back").remove();
	$('.image-button.next').click(function(){
		//console.log('click');
		$(this).fadeOut(200).delay(2000).fadeIn(200);
	});

}

function useChannelForSection(){
	//DOC div.sp.column.one, .ui-widget-header h1 -- IF .ui-widget-header h1 DOES NOT EXIST ADD ChannelName
	if(!$("div.sp.column.one .ui-widget-header h1").length) {
		$("div.sp.column.one .ui-widget-header").append("
Parents");
	}
}

function dynamicContent(){
	//MOD MYSTART
	var imagePath1 = "";
   	$(".sw-mystart-button.signin a span").empty().append(imagePath1);
   	var imagePath2 = "";
	$(".sw-mystart-button.register a span").empty().append(imagePath2);
	var imagePath3 = ""; 
	$(".sw-mystart-button.manage a span").empty().append(imagePath3);
	//$("#sw-mystart-mypasskey a span").empty().append("");
	var imagePath4 = "";	 
	$("#sw-myaccount-list li a span").eq(0).empty().append(imagePath4);
	var imagePath5 = "";
	$("#sw-myaccount-list li a span").eq(1).empty().append(imagePath5);
	$("#profile").prepend("

");
	$("#sw-myaccount-list li a").eq(0).appendTo($(".sw-mystart-button.access"));
	$("#sw-myaccount-list li a").eq(0).appendTo($(".sw-mystart-button.signout"));
    $(".sw-mystart-button.signout a, .sw-mystart-button.access a").removeAttr("tabindex");
	$(".sw-mystart-dropdown.schoollist").appendTo($("#school-dropdown"));
	addFramesetTranslate();
	$("#sw-mystart-search").appendTo("#gb-mystart-right");
    $("#sw-mystart-search .hidden-label").text('Search');
	$("#sw-search-button").addClass("icon-font").html("5");
	$("#gb-mystart-bar").show();

    $(".sw-mystart-dropdown.schoollist").focus(function() {
        $(".sw-dropdown",this).attr({
            "aria-hidden": "false"
        }).show();
        $(".sw-mystart-dropdown.schoollist li:first-child a").focus();
    });

    $(".sw-mystart-button.signout").focus(function() {
    	$(".sw-mystart-dropdown.schoollist .sw-dropdown").attr({
        	"aria-hidden": "true"
        }).hide();
    });

    $(".sw-mystart-dropdown.schoollist .sw-dropdown-list li a").keydown(function(e) {
    	switch(e.keyCode) {
        	// CONSUME TAB KEY
            case csKeyCodes.tab:
                e.preventDefault();
                //CLOSE DROPDOWN
                $(".sw-mystart-dropdown.schoollist .sw-dropdown").hide();


                	$(".sw-mystart-button.signin > a").focus();

            break;
        }
    });

	//DOC .ui-breadcrumb-first -- CHANGE THE FIRST BREADCRUMB TEXT TO HOME
	$("li.ui-breadcrumb-first > a > span").text("Home");

	//DOC #swsignin-txt-username -- AUTO-FOCUS USERNAME SIGNIN FORM FIELD #swsignin-txt-username
	$("#swsignin-txt-username").focus();

	//DOC div.ui-spn > div > p > span -- IF ON SIGNED OUT PAGE ADD STYLING
	if($("div.ui-spn > div > p > span").text() == "You have been signed out."){
		$("div.ui-spn > div").css({"text-align" : "center", "padding" : "50px 0px 50px 0px"});
	}

	//ADD CLASS OF FIRST TO FIRST CHANNEL
	$("li.sw-channel-item:eq(0)").addClass("first");

	//MOD EVENTS
	$(".upcomingevents").modEvents({
		columns:"yes",
		monthLong:"no"
	});

	// ********** FOOTER ********** //
	//DOC get Schoolwires footer links
	var feedbackURL = $("div#sw-footer-links li:eq(0) a").attr("href");
	var termsURL = $("div#sw-footer-links li:eq(2) a").attr("href");
	var policyURL = $("div#sw-footer-links li:eq(1) a").attr("href");

	$("a#feedback").attr("href", feedbackURL);
	$("a#terms").attr("href", termsURL);
	$("a#policy").attr("href", policyURL);			
}

function searchText() {
	//DOC #sw-mystart-search, #sw-search-input -- SETS THE VALUE OF #sw-search-input
        //SEARCH TEXT 
        $("div#sw-mystart-search input#sw-search-input").removeAttr("placeholder");
	$("div#sw-mystart-search input#sw-search-input").attr("value", "Search...");
	$("div#sw-mystart-search input#sw-search-input").focus(function() {
		if($(this).val() == "Search...") {
			$(this).val("");
		}
	});
	//DOC #sw-mystart-search, #sw-search-input -- RESET THE VALUE OF #sw-search-input
	$("div#sw-mystart-search input#sw-search-input").blur(function() {
		if($(this).val() == "") {
			$(this).val("Search...");
		}
	});
}

function addFramesetTranslate() {
		var pageURL = window.location.href;
		var translateDropdown = '
' +
							'
Translate this page...' +
							'
' +
								'
Translate this page...' +
								'' +
									'
' +
										'English to Spanish' +
									'' +
									'
' +
										'English to Arabic' +
									'' +
									'
' +
										'English to Chinese (Simplified)' +
									'' +
									'
' +
										'English to Chinese (Traditional)' +
									'' +
									'
' +
										'English to French' +
									'' +
									'
' +
										'English to German' +
									'' +
									'
' +
										'English to Italian' +
									'' +
									'
' +
										'English to Japanese' +
									'' +
									'
' +
										'English to Korean' +
									'' +
									'
' +
										'English to Portuguese' +
									'' +
									'
' +
										'English to Russian' +
									'' +
								'' +
							'' +
							'
' +
						'';
		var pageHost = window.location.host;
		if(pageHost != "translate.googleusercontent.com") {				
			$("#gb-mystart-right").prepend(translateDropdown);
			$(".sw-mystart-dropdown.translate").click(function()
			{
				$(".sw-dropdown",this).attr({
                	"aria-hidden": "false"
                }).show();
			});
			$(".sw-mystart-dropdown.translate").hover(function()
			{
			},
			function()
			{
				$(".sw-dropdown",this).attr({
                	"aria-hidden": "true"
                }).hide();
			});

            $(".sw-mystart-dropdown.translate").focus(function() {
            	$(".sw-dropdown",this).attr({
                	"aria-hidden": "false"
                }).show();
                $(".sw-mystart-dropdown.translate li:first-child a").focus();
            });   

            $(".sw-mystart-dropdown.translate li a").keydown(function(e) {
				// CAPTURE KEY CODE
                switch(e.keyCode) {
                    // CONSUME LEFT AND UP ARROWS
                    case csKeyCodes.left:
                    case csKeyCodes.up:
                        e.preventDefault();

                        // IS FIRST ITEM
                        if($(this).parent().is(":first-child")) {
                            // FOCUS DROPDOWN BUTTON
                            $(".sw-mystart-dropdown.translate .selector").focus();
                        } else {
                            // FOCUS PREVIOUS ITEM
                            $(this).parent().prev("li").find("> a").focus();
                        }
                    break;

                    // CONSUME RIGHT AND DOWN ARROWS
                    case csKeyCodes.right:
                    case csKeyCodes.down:
                        e.preventDefault();

                        // IS LAST ITEM
                        if($(this).parent().is(":last-child")) {
                            // FOCUS FIRST ITEM
                            $(".sw-mystart-dropdown.translate .sw-dropdown-list li:first-child > a").focus();
                        } else {
                            // FOCUS NEXT ITEM

                            $(this).parent().next("li").find("> a").focus();
                        }
                    break;

                    // CONSUME HOME KEY
                    case csKeyCodes.home:
                        e.preventDefault();

                        // FOCUS FIRST ITEM
                        $(".sw-mystart-dropdown.translate .sw-dropdown-list li:first-child > a").focus();
                    break;

                    // CONSUME END KEY
                    case csKeyCodes.end:
                        e.preventDefault();

                        // FOCUS LAST ITEM
                        $(".sw-mystart-dropdown.translate .sw-dropdown-list li:last-child > a").focus();
                    break;

                    // CONSUME TAB KEY
                    case csKeyCodes.tab:
                        e.preventDefault();
                        //CLOSE DROPDOWN
                        $(".sw-mystart-dropdown.translate .sw-dropdown").hide();
                        // FOCUS NEXT ITEM
                        $("#sw-search-input").focus();
                    break;

                }
            });

            $("#sw-search-input").focus(function() {
            	$(".sw-mystart-dropdown.translate .sw-dropdown").attr({
                	"aria-hidden": "true"
                }).hide();
            });
		}

}

