	$(document).ready(function() {
		//encode filepath as suggested by Google and set src attribute of iframe to that encoded url
        var filePath = "https://www.duplinschools.net//cms/lib/NC01001360/Centricity/Domain/3044/considerations-for-school-closure whole.pdf";
        
        if (filePath !== ''){
        	$("#doc-viewer-text-29351").removeClass("ui-helper-hidden");
        }
		
        filePath = encodeURIComponent(filePath);
       
        var url = "https://docs.google.com/viewer?url=" + filePath + "&embedded=true";
        
        $("#doc-viewer-29351").attr("src", url);
	});

