div.script:
var t;
var elem = document.querySelector('.recent-news p');
var $elem = $(elem);
var html = $elem.find('span').text() + '   |   ' + $elem.find('span').text();

if(elem.offsetHeight < elem.scrollHeight || elem.offsetWidth < elem.scrollWidth) {
	$elem.css('text-overflow','clip').find('span').html(html);

	t = setInterval(function(){
		moveTicker($elem.find('span'));
	}, 75);

	$elem.hover(function(){
		clearInterval(t);
	}, function(){
		t = setInterval(function(){
			moveTicker($elem.find('span'));
		}, 75);
	});
}

function moveTicker(elem) {
	elem.css('margin-left', '-=1px');
}

header.script:
function set_welcome_to_height() {
	var logo = $('.logo');
	var welcome_to = $('.welcome-to');
	var padding = (logo.height() - welcome_to.height()) / 2;
	welcome_to.css({
		'padding-top' : padding,
		'padding-bottom' : padding
	});
}

set_welcome_to_height();
$(window).resize(function(){ set_welcome_to_height(); });

section.script:
			$.ajax({
				url: 'https://cardinalschools.org/wp-content/themes/cardinal-schools/php/tweetledee/userjson.php?c=2&user=cardinalhuskies&xrp=1&cache_interval=300',
				dataType: 'json',
				async: true,
				success: function(data){
					console.log(data);
					var container = $('#twitter-feed');
					var html = '';

					for(var i = 0; i < data.length; i++) {
						var tweet = data[i];
						//var text = jQuery.trim(tweet.text).substring(0, 128).split(" ").slice(0, -1).join(" ") + "...";

						html += '
';
							html += '
' + get_formatted_date(tweet.created_at) + '';
							html += '
' + linkify(tweet.text) + '';
						html += '';
					}

					$('#twitter-feed').html(html);
				}
			});

			function linkify(text) {
				var urlRegex = /(\b(https?|ftp|file):\/\/[-A-Z0-9+&@#\/%?=~_|!:,.;]*[-A-Z0-9+&@#\/%=~_|])/ig;
				return text.replace(urlRegex, function(url) {
					return '' + url + '';
				})
				// or alternatively
				// return text.replace(urlRegex, '$1')
			}

