        $(".mega-channel-cat-section-list.one .mega-channel-cat-section-list-item a").keydown(function(e) {
            switch(e.keyCode){
                // CONSUME LEFT AND UP ARROWS
                case csKeyCodes.up:
                    e.preventDefault();

                    if($(this).parent().is(":first-child")) { 
                        //$(this).parent().parent().find("li:first-child a").focus();
                        $(".mega-channel-cat-section-list:last-child .mega-channel-cat-section-list-item:last-child a").focus();
                    } else { 
                        $(this).parent().prev("li").find("a").focus();
                    } 
                break;
                case csKeyCodes.down:
                    e.preventDefault();

                    if($(this).parent().is(":last-child")) { 
                    	if(!$(".mega-channel-cat-section-list.two").length) {
                        	$(".mega-channel-cat-section-list.one .mega-channel-cat-section-list-item:first-child a").focus();
                        } else {
                    		$(this).parent().parent().parent().parent().next().find(".mega-channel-cat-section-list-item:first-child a").focus();
                        }
                    } else { 
                        $(this).parent().next("li").find("a").focus();
                    }
                break;

                // CONSUME HOME KEY
                case csKeyCodes.home:
                    e.preventDefault();

                    // FOCUS THE FIRST ITEM
                    $(".mega-channel-cat-section-list.one .mega-channel-cat-section-list-item:first-child a").focus();
                break;

                // CONSUME END KEY
                case csKeyCodes.end:
                    e.preventDefault();

                    // FOCUS THE LAST ITEM
                    $(".mega-channel-cat-section-list:last-child .mega-channel-cat-section-list-item:last-child a").focus();
                break;
            }
        });
        $(".mega-channel-cat-section-list.two .mega-channel-cat-section-list-item a").keydown(function(e) {
            switch(e.keyCode){
                // CONSUME LEFT AND UP ARROWS
                case csKeyCodes.up:
                    e.preventDefault();

                    if($(this).parent().is(":first-child")) { 
                        $(this).parent().parent().parent().parent().prev().find(".mega-channel-cat-section-list-item:last-child a").focus();
