                    $("#gb-search-input-cont").addClass("open").attr({
                    	"aria-expanded": "true",
                        "aria-hidden": "false"
                    });
                    $(".close-search-btn").attr("tabindex","0");
                    $("#sw-search-input").attr("tabindex","0");
                    setTimeout(function() {
                        $("#sw-search-input").focus();
                    }, 300);
                }
            }
        });
        $(".close-search-btn").click(function() {
        	$("#gb-search-input-cont").removeClass("open").attr({
                "aria-expanded": "false",
                "aria-hidden": "true"
            });
