                // 3) Run the AJAX call and do something with the response
                OHIO.ODX.actions.getAjaxDataFromURL(serviceURL).then(function (response) {
                    var alertData = JSON.parse(response);
                    var $alert = $('.odx-site-alert');
                    if (!$.isEmptyObject(alertData)) {

                        var $label = $alert.find('.odx-site-alert__label');
                        var $linkElement = $alert.find('.odx-site-alert__view-more');
                        var alertTypeArray = alertData.alertType.split(',');
                        var indexOfActive = alertTypeArray.indexOf('active-alert');

                        alertTypeArray.splice(indexOfActive, 1);
                        color = alertTypeArray[0];

                        $label.html(alertData.title);
                        $linkElement.attr('href', alertData.url);
                        $alert.addClass('odx-site-alert--' + color);
                        $alert.closest('.header').addClass('header--' + color);
                        $alert.find('.sr-only').removeClass('sr-only');
                        $alert.find('.no-remove-sr-only').addClass('sr-only');
                        $('.header').show();
                        $('.navigation').addClass('alert-is-visible');
                        $('.content-body').addClass('alert-is-visible');
                    } else {
                        $alert.remove();
                        if(window.siteId == "site"){
                            //$('.content-body').css('margin-top', '-24px');
                        }

                    }

                },
                    function (error) {
                        console.error(error);
                    }
                );
   }
            });

        })(jQuery);

