(function() {
    $(document).on('ready', function() {

        if (window.siteId !== 'odh') return;

        $('.odx-az-index').show();
        var generateAlphabet = function(c1, c2) {
            var letters = 'abcdefghijklmnopqrstuvwxyz'.split('');
            return (letters.slice(letters.indexOf(c1), letters.indexOf(c2) + 1));
        }

        var alphabeth = generateAlphabet('a', 'z');
        var $alphabethContainer = $('.js-alphabet-container:visible .odx-az-index__alphabet-menu-container');
        var length = alphabeth.length;

        for (var i = 0; i < length; i++) {
            $alphabethContainer.append('' + alphabeth[i] + '');
        }

        $alphabethContainer.append('×');

        var $letterButtons = $alphabethContainer.find('.odx-az-index__letter-button');
        $letterButtons.on('click',function(e){
        	e.preventDefault();
        	/*var landingPageUrl = 'https://odh.ohio.gov/wps/portal/gov/odh/home/?1dmy&urile=wcm%3apath%3a/ohio+content+english/odh/home/a-to-z-index/a-to-z-index';*/

                var landingPageUrl = "/wps/wcm/connect/gov/Ohio+Content+English/odh/home/a-to-z-index/a-to-z-index";

                var path = landingPageUrl.indexOf("connect") >= 0 ? landingPageUrl.replace(/.*\/wps\/wcm\/connect\/gov\//, "") : landingPageUrl.replace(/.*\/wps\/wcm\/connect\/gov\//, "");
                landingPageUrl = '?1dmy&urile=wcm%3apath%3a%2F' + encodeURIComponent(path);

        	var $button = $(this);
        	var letter = $button.text().trim();
        	window.location.replace(landingPageUrl + '#' + letter);
        })


        var toggleMenu = function(){
        	var $mainContainer = $('.navigation');

        	var $button = $('.odx-az-index__button');

        	var $menuContainer = $('.js-alphabet-visible');
            if($menuContainer.is(':visible')){
            	$menuContainer.fadeOut(400,'swing',function(){
            		$mainContainer.css('overflow','hidden');
            	});
            	$button.removeClass('odx-az-index__button--active');	
            }else{
            	$mainContainer.css('overflow','visible');
            	$menuContainer.fadeIn();
            	$button.addClass('odx-az-index__button--active');
            }
        };

        $('.odx-az-index__close-button').on('click',function(){
        	toggleMenu();
        });


        $('.odx-az-index__button button').on('click', function() {
            var $navContainer = $('.navigation');
            var $menuContainer = $('.js-alphabet-container:visible .odx-az-index__alphabet-menu');
            $menuContainer.addClass('js-alphabet-visible')
            $navContainer.append($menuContainer);
            toggleMenu();

        })


    })
})();

(function ($) {
    $(document).ready(function () {
        var $searchButton = $('#search-button'),
            $searchForm = $('#search-form'),
            $searchTop = $('.odx-search'),
            $closeSearch = $('.odx-search__close'),
            $input = $('.odx-search .odx-search__input'),
            $loop = $('.odx-search__loop-btn'),
            searchURLPortal = searchUrl.replace('&', '&');

        $(document).on('click', function (event) {
            var $target = $(event.target);
            var isClickInside = $target.closest($searchForm).length;
            var isSearchButtonClicked = $target.closest($searchButton).length;
            if (!isClickInside && !isSearchButtonClicked) $searchTop.removeClass('in');
        });

        $searchButton.on('click', function () {
            $searchTop.addClass('in');
            $input.focus();
        });

        $searchButton.on('keypress', function (event) {
            var keyCode = event.which;
            if (keyCode == 13) {
                $searchButton.trigger('click');
            }
        });

        $closeSearch.on('click', function () {
            $searchTop.removeClass('in');
        });

        $closeSearch.on('keypress', function (event) {
            var keyCode = event.which;
            if (keyCode == 13) {
                $closeSearch.trigger('click');
            }
        });

        $searchForm.attr("action", searchURLPortal);
        $('#js-go-help').attr("href", window.globalLinks.help);
    });
})(jQuery);

