(function($) {
    var baseURIForPortalLocations = 'taxonomy/portal-locations/all-portal-locations/odx-agency/site-components/';

    function createUIComponent(selector, categories) {

        // 1) Get all variables to support multilingual content
        var currentLanguage = OHIO.ODX.actions.getUserLanguageByCookie();

        // Get the library according to the user's language
        var wcmLibrary = (typeof window.WCMLibraries !== "undefined") ? window.WCMLibraries[currentLanguage] : window.WCMLibraries['en'];
        var virtualPortal = (window.portalContext !== 'none') ? window.portalContext + '/' : 'gov';

        // 2) Configure parameters for AJAX call
        var params = {
            source: 'library',
            srv: 'cmpnt',
            cmpntid: '3186924c-7236-43df-84b7-7251173633df',
            'WCM_Page.ResetAll': 'TRUE',
            CACHE: 'NONE',
            CONTENTCACHE: 'NONE',
            CONNECTORCACHE: 'NONE',
            location: wcmLibrary + '/' + window.siteId + '/odx-social-networks',
            categories: $.isArray(categories) ? categories.join(',') : categories,
        };
        var serviceURL = '/wps/wcm/connect/gov/odx%20common%20design/utility/context?' + $.param(params);
        var $containerForDynamicData = $(selector);

        // 3) Run the AJAX call and do something with the response
        OHIO.ODX.actions.getAjaxDataFromURL(serviceURL).then(function(response) {
            var items = JSON.parse(response);
            var component = new OhioToolkit.components.WebComponent({
                element: selector,
                templateLocation: '/wps/wcm/connect/gov/f681e1f1-e5d8-4942-ae23-bff71182337a/odx-social-networks.html?MOD=AJPERES&CACHEID=ROOTWORKSPACE.Z18_M1HGGIK0N0JO00QO9DDDDM3000-f681e1f1-e5d8-4942-ae23-bff71182337a-mrM.fFp',
                data: {
                    items: items
                }
            });

            component.render().then(function() {

            });

        }, function(error) {
            console.error(error);
        });
    }

    function onReady() {
        var selectorToPlaceDynamicHTML = '#odx-social-network';
        createUIComponent(selectorToPlaceDynamicHTML, baseURIForPortalLocations + "odx-social-network");
        if (typeof agencyLogo !== "undefined") {
            $(".odx-home-logo").attr("src", agencyLogo).removeClass("hidden");
        }
        $(".odx-home-link").attr("href", "?1dmy&urile=wcm%3apath%3a%2FOhio%2BContent%2BEnglish%2Fodh");
    }

    $(document).ready(onReady);

})(jQuery);

