        function CustomizeMenuItem() {
            if (screen.width <= 767) {
                var newheader = $(".navbar-custom").eq(0).find('ul').find('li.secondary-tab').clone();
                $(".navbar-custom").eq(0).find('ul').find('li.secondary-tab').remove();
                $(".navbar-custom").eq(0).css("display", "none");
                $('.header_right .navbar ul li.language').css("display", "none");
                $(".navbar-collapse").eq(0).find('ul').prepend(newheader);
            }
            else {
                var newheader = $(".navbar-collapse").eq(0).find('ul').find('li.secondary-tab').clone();
                $(".navbar-collapse").eq(0).find('ul').find('li.secondary-tab').remove();
                $(".navbar-custom").eq(0).css("display", "block");
                $('.header_right .navbar ul li.language').css("display", "block");
                $(".navbar-custom").eq(0).find('ul').prepend(newheader);
            }
        }

