        function checkwindowtype() {

            if (screen.width <= 480) {
                {
                    mymobile = true;
                    document.getElementById("mobilediv1").style.display = "block";
                    document.getElementById("desktopdiv1").style.display = "none";
                }
            }
            else {
                {
                    mymobile = false;
                    document.getElementById("mobilediv1").style.display = "none";
                    document.getElementById("desktopdiv1").style.display = "block";
                }

            }

        }

