    function arrowLocation() {
        var h = (914.5 / 1850) * window.innerWidth - (74825 / 1850) - 8;
        h = $(".slideshow_outer_container").height() - 40;
        $(".slideshow_container .slick-prev").css("top", h.toString() + "px");
        $(".slideshow_caption_container_outer").css("top", (h - 80).toString() + "px");
    }
    $(document).ready(function () {
        $(".slideshow_container .slick-dots").wrap("
");
        $(".slideshow_container .slick-arrow").wrap("
");
        setTimeout(arrowLocation, 50);
        setTimeout(arrowLocation, 100);
    });
    $(window).resize(function () {
        setTimeout(arrowLocation, 50);
        setTimeout(arrowLocation, 100);
    });
    var h = 0;
    var origH = 0;
    //setInterval(arrowLocation, 1);
    function setSlideshowItems() {
        h = window.innerHeight - 300;
        $(".slideshow_outer_container").css("height", "");
        $(".slideshow_caption_container_outer").css("bottom", "");
        $(".slideshow_outer_container .slick-next").css("bottom", "");
        $(".slideshow_outer_container .slick-dots").css("bottom", "");
        origH = $(".slideshow_outer_container").height();
        if (h < origH) {
            if (h >= 450) {
                $(".slideshow_outer_container").css("height", (h + 1).toString() + "px");
                $(".slideshow_outer_container .slick-next").css("bottom", (origH - (h + 21) + 40) + "px");
                $(".slideshow_outer_container .slick-dots").css("bottom", (origH - (h + 21 + 7) + 40) + "px");
            }
            else {
                $(".slideshow_outer_container").css("height", "451");
                $(".slideshow_outer_container .slick-next").css("bottom", (origH - (451 + 20) + 40) + "px");
                $(".slideshow_outer_container .slick-dots").css("bottom", (origH - (450 + 21 + 7) + 40) + "px");
            }
        }
        if (origH < $(".slideshow_outer_container").height()) {
            $(".slideshow_outer_container").css("height", origH.toString() + "px");
            $(".slideshow_outer_container .slick-next").css("bottom", "20px");
            $(".slideshow_outer_container .slick-dots").css("bottom", "13px");
        }
    }
    //var reset = 0;
    $(document).ready(function () {
        setTimeout(setSlideshowItems, 50);
        setTimeout(setSlideshowItems, 100);
    });
    $(window).resize(function () {
        setTimeout(setSlideshowItems, 50);
        setTimeout(setSlideshowItems, 100);
    });
