var equation = "";
var canvas = document.createElement("canvas");
canvas.width = 1000;
canvas.height = 1000;
$(canvas).css({"position": "fixed", "top": "50%", "left": "50%", "transform": "translateX(-50%) translateY(-50%)", "z-index": "5555555", "pointer-events": "none", "width": "100vmin", "height": "100vmin"});
var ctx = canvas.getContext('2d');
$(canvas).appendTo("body");
var x = -500;
ctx.strokeStyle = "#000";
setInterval(function () {
    if (equation != "") {
        ctx.lineWidth = 2;
        y = -1 * eval(equation) + 500;
        ctx.beginPath();
        ctx.moveTo(x + 500, y);
        x += 2;
        y = -1 * eval(equation) + 500;
        ctx.lineTo(x + 500, y);
        ctx.stroke();
    }
}, 10);
