function waitForElement() {
    if (typeof videojs !== "undefined") {
        videojs.options.flash.swf = "//vjs.zencdn.net/5.8.0/video-js.swf";
        videojs('video', {}, function () { this.ga(); });
    }
    else setTimeout(function () { waitForElement(); }, 1);
}

div.script:
                $(document).scroll(function () {
                    var y = $(document).scrollTop(), //get page y value 
                    header = $(".StickyNav");
                    contentarea = $(".contentarea");
                    if (y >= 200) {
                        header.css({ position: "fixed", "top": "0" }),
		                header.css({ width: "100%" });

                    } else {
                        header.css("position", "absolute"),
                        header.css("top", "115px"),
		                header.css("width", "100%"),
                        contentarea.css("position", "relative"),
                        contentarea.css("top", "36px");
                    }
                });

