div.script:
$(document).ready(function () {
    $("#5877553f-fa8f-422a-a505-22872d6eac22").owlCarousel({
        navigation: false,
        pagination : false,
        autoPlay: getAutoPlayValue(),
        transitionStyle : "fade",
        items: '10',
        mouseDrag: false,
        singleItem: true});

    function getAutoPlayValue() {
        var autoPlayValue = 6000;
        if (autoPlayValue == 0) {
            autoPlayValue = false;
        }
        return 8 > 1 ? autoPlayValue : false;
    }
});

$(document).ready(function () {
    $("#99d8d4e4-6719-4b48-b033-14a2116211ef").owlCarousel({
        navigation: true, // Show next and prev buttons
        navigationText: [
             "",
             ""
             ],
        items: '10',
        autoPlay : getAutoPlayValue(),

        mouseDrag: false,
        pagination:true,
        paginationNumbers: false,
        responsive:true,
        singleItem: true});

    $('div.promotional-carousel .owl-controls').on('click', function () {
        $('#99d8d4e4-6719-4b48-b033-14a2116211ef').trigger('owl.stop')
    })

    $('div.promotional-carousel').keyup(function (e) {
        if (e.keyCode == 9 || e.which == 9) {
            $('#99d8d4e4-6719-4b48-b033-14a2116211ef').trigger('owl.stop');
        }
    });

    // Prevent default on click of owl pagination
    $('.owl-page').on('click', '[href]', function(event){
        event.preventDefault();
    });

    function getAutoPlayValue() {
        var autoPlayValue = 10000;
        if (autoPlayValue == 0) {
            autoPlayValue = false;
        }
        return 8 > 1 ? autoPlayValue : false;
    }

});

jQuery(document).ready(function () {
    jQuery('#9884c7ba-3578-4358-80e1-32752d325021').hide();
    jQuery('#9884c7ba-3578-4358-80e1-32752d325021').listSolrResults({
        currentPagePath: '/content/schools/ohva/en',
        label: 'News',
        listFrom: 'solr',
        limit: '3',
        listType: 'news',
        listOrder: 'desc',
        stateAbbr: '',
        viewAllText: 'View All',
        viewAllUrl: '/news.html',
        viewAllTitle: 'View All',
        uuid: '9884c7ba-3578-4358-80e1-32752d325021',
        isEditMode: 'false',
        paginate: 'false',
        entriesPerPage: '10'
    });
});

