    $ohioGovMenu.on('click', function() {
      $body.addClass('full-screen-takeover');
      $backdrop.css("visibility ", "visible ");
    });

    $closeTopNavOhio.on('click', function() {
      $body.removeClass('full-screen-takeover');
      $backdrop.css("visibility ", "hidden ");
    });

    $body.on('click', function(ev) {
      var touchedElement = $(ev.target);
      var isToggler = $(touchedElement).hasClass('ohio-external-nav-open-icon');
      var component = $('.ohio-takeover-wrapper');
      if ($(touchedElement).closest(component).length === 0 && !isToggler) {
        $body.removeClass('full-screen-takeover');
      }
    });

    var $searchButton = $('#global-search-button'),
      $input = $('#search-form-global .ohio-search-field'),
      globalSearchURLPortal = "https://ohio.gov/wps/portal/gov/site/search/?1dmy&urile=wcm%3apath%3a/Ohio+Content+English/ohio.gov/search ";

