$(document).ready(function(){
    var stopCycles = 0;
    $('.odx-full-banner').slick({
        arrows          : false,
        dots            : true,
        autoplay        : true,
        autoplaySpeed   : 9000,
        responsive :[{
            breakpoint: 767,
            settings: {
                    slidesToShow: 1,
                    slidesToScroll: 1,
                    dots: true
            }
        }]
    }).removeClass('odx-hidden-visibility');
    if(stopCycles != 0){
        var slider = jQuery('.odx-full-banner'),
            cycleCount = 0;
        slider.on('afterChange', function(event, slick, direction){
            var currentSlide = slider.slick('slickCurrentSlide')+1;
            if(cycleCount === stopCycles){
                slider.slick('slickSetOption', 'pauseOnHover', false);
                slider.slick('slickSetOption', 'pauseOnDotsHover', false);
                slider.slick('slickPause');
            }
            else if(currentSlide == slick.slideCount){
                cycleCount++;
            }
        });
    }
});

