(function ($) {
    $(document).ready(function () {
        $(".covid-video-gallery__item").on( "click", function() {
            var selectedVideo = $( this ),
                selectedVideoURL = selectedVideo.data("url"),
                iframe = document.createElement('iframe');
            $(".covid-video-gallery__item").removeClass("active");
            $( this ).addClass("active");
            iframe.classList.add("embed-responsive-item")
            iframe.src = selectedVideoURL;
            $(".covid-video-gallery__player .embed-responsive").empty();
            $(".covid-video-gallery__player .embed-responsive").append( iframe );
        });
        $(".covid-video-gallery__item").first().trigger("click").trigger("touchstart");
    });
}(jQuery));

