(function( $ ) {
	$( document ).on( 'ready', function() {

		// 1) Get all variables to support multilingual content
		var currentLanguage = OHIO.ODX.actions.getUserLanguageByCookie();
		// Get the library according to the user's language
		var wcmLibrary = (typeof window.WCMLibraries !== "undefined") ? window.WCMLibraries[ currentLanguage ] : window.WCMLibraries[ 'en' ];
		var virtualPortal = (window.portalContext !== 'none') ? window.portalContext + '/' : 'gov';

		var location = [];
		var locationPath = '';
		var currentPathArray = decodeURIComponent("?1dmy&urile=wcm%3apath%3a%2FOhio%2BContent%2BEnglish%2Fcovid-19%2Fhome").split("/");
		var length = currentPathArray.length;
		currentPathArray.forEach(function(element, index){
			if(index > 1 ){
				locationPath += "/" + element;   
			}
		});
		location.push(wcmLibrary + locationPath + '/forms'); 

		var lawsAndRules = '';
		var urlToLawsAndRules = window.globalLinks.lawsAndRules[window.siteId] || false;
		// If there is "Global Laws and Rules" page we display the link to see more...
		if( urlToLawsAndRules ) {
			var lawsAndRules = urlToLawsAndRules.split("gov");
			lawsAndRules = lawsAndRules[1];
			location.push(wcmLibrary + lawsAndRules); 
		}

		var currentPortalLocations = [];
		var currentPortalLocationsArray = ''; //'';
		currentPortalLocationsArray = currentPortalLocationsArray.split( ',' );
		for( var i = 0; i < currentPortalLocationsArray.length; i ++ ) {
    		//if( currentPortalLocationsArray[ i ].match('/programs/[A-Z0-9a-z]*/') != null){
        		currentPortalLocations.push(currentPortalLocationsArray[ i ]);
    		//} 
		}

		// 2) Configure parameters for AJAX call
		var params = {
			source: 'library',
			srv: 'cmpnt',
			cmpntid: 'c914254f-36b0-4401-b76c-29ab368c002c',
			// location: wcmLibrary + '/' + location
			categories: $.isArray(currentPortalLocations) ? currentPortalLocations.join(',') : currentPortalLocations,
			location: $.isArray(location) ? location.join(',') : location 
		};

		var serviceURL = '/wps/wcm/connect/gov/odx%20common%20design/utility/context?' + $.param( params );
		// 3) Run the AJAX call and do something with the response
		OHIO.ODX.actions.getAjaxDataFromURL( serviceURL ).then( function( response ) {
			var resources = JSON.parse( response );

			if (resources.length > 0){
                var component = new OhioToolkit.components.WebComponent( {
				element: '.js-odx-related-laws-and-forms',
				templateLocation: '/wps/wcm/connect/gov/f27fcbe8-153b-4e5f-9f7f-94f7f353183e/related-laws-and-forms.hbs.html?MOD=AJPERES&CACHEID=ROOTWORKSPACE.Z18_M1HGGIK0N0JO00QO9DDDDM3000-f27fcbe8-153b-4e5f-9f7f-94f7f353183e-mh7NPKp',
				data: {
					items: resources,
					relatedLawsAndForms: 'odx-related-laws-and-forms',
					viewMoreLawsAndForms: 'odx-view-more-laws-and-forms',
					fullListLawsAndForms: 'odx-full-list-laws-and-forms',
				}
			});

			component.render().then( function() {
				console.log( 'Rendered!' );
				var linkToLawsAndRules = window.globalLinks.lawsAndRules[window.siteId] || false;
				// If there is "Global Laws and Rules" page we display the link to see more...
				if( linkToLawsAndRules ) {
					$('.js-link-view-other-laws-container').removeClass('hidden');
					$('.js-link-view-other-laws').attr('href', linkToLawsAndRules);
				}
				if($('#js-more-forms-container a').length > 0){
					$('.js-more-forms-link').removeClass('hidden');
				}

				$(".js-more-forms-link").click(function(){
        			if($(this).find("i").hasClass("fa-chevron-down")){
           				$(this).find("i").removeClass("fa-chevron-down");
           				$(this).find("i").addClass("fa-chevron-up");
        			} else{
           				$(this).find("i").removeClass("fa-chevron-up");
           				$(this).find("i").addClass("fa-chevron-down");
        			}
    			});
			});

                        }
		},
		function( error ) {
			console.error( error );
		});
	});

	})( jQuery );

div.script:
(function ($) {
    $(document).ready(function () {
        $('.js-popular-topics-carousel').not('.slick-initialized').each(function( index ) {
            $( this ).slick({
                infinite: false,
                arrows: true,
                slidesToShow: 4,
                slidesToScroll: 4,
                prevArrow: $( this ).parents('.js-odx-carousel-cards').find('#js-popular-topics-button-left .js-carousel-button-card__left.left-nav-card-carousel'),
                nextArrow: $( this ).parents('.js-odx-carousel-cards').find('#js-popular-topics-button-right .js-carousel-button-card__right.right-nav-card-carousel'),
                responsive: [
                {
                    breakpoint: 1200,
                    settings: {
                        slidesToShow: 3,
                        slidesToScroll: 3
                    }
                },
                {
                    breakpoint: 992,
                    settings: {
                        slidesToShow: 2,
                        slidesToScroll: 2
                    }
                },
                {
                    breakpoint: 580,
                    settings: {
                        slidesToShow: 1,
                        slidesToScroll: 1
                    }
                }]
            }).removeClass('odx-hidden-visibility');
        });
    });
}(jQuery));

(function(){
    var globalLinks = {};
    globalLinks.login = '/wps/portal/gov/'+siteId+'/login';
    globalLinks.help = '/wps/portal/gov/'+siteId+'/help-center';
    globalLinks.createAccount = '/wps/portal/gov/registration';
    globalLinks.liveChat = '/wps/portal/gov/home/live-chat';
    globalLinks.homepageMainImage = '/wps/portal/gov/home/';
    globalLinks.home = '/wps/portal/gov/'+siteId+'/home';

    // Add here all agencies that support a global laws and rules page
    globalLinks.lawsAndRules = {
        'odh' : '/wps/portal/gov/' + siteId + '/health-rules-laws-and-forms'
    };



    window.globalLinks = globalLinks;

    // Create libraries names to use as parameters in the URLs or cookies 
    // to get the error messages depending on user language
    var WCMLibraries = {
        "en" : "Ohio Content English",
        "es" : "Ohio Content Spanish"
    };

    window.WCMLibraries = WCMLibraries;
})();

(function($) {
    $(function() {
        $(".js-odx-secondary-footer__item").each(function(i, element) {
            var splitURL = $(element).attr("href").replace(/ /g, "+").split("/");
            splitURL[2] = siteId;
            splitURL = splitURL.join("/");
            $(element).attr("href", splitURL);
        });
        $('.footer-bar__logo img').each(function(){
            var imgSrc = $(this).attr('src');
            imgSrc = imgSrc.replace('myconnect','connect');
            $(this).attr('src',imgSrc);
        });
    });
})(jQuery);

body.script:
//Override External URLS
$('#odx-main-content a[href^="http"],#odx-main-content a[href^="https"]').attr("target","_blank");

window.onscroll = function() {odxScroll()};
function odxScroll() {
    if (document.body.scrollTop > 30 || document.documentElement.scrollTop > 30) {
    	$(".navigation").addClass("sticky-header");
    } else {
    	$(".navigation").removeClass("sticky-header");
    }
}

// Add offsett when scrolling in tablet and mobile
window.addEventListener("hashchange", function () {
   var scrollY = window.scrollY;
    if(window.scrollY == undefined){
       scrollY = window.pageYOffset;
    }
   if(scrollY !== 0 && $(window).width() < 992){
    window.scrollTo(window.scrollX, window.scrollY - 150);
   }
});

