link.script:
        var $timer = $("#lblTimeoutTimer");
            function update() {
                var myTime = $timer.html();
                var ss = myTime.split(":");
                var dt = new Date();
                dt.setHours(0);
                dt.setMinutes(ss[0]);
                dt.setSeconds(ss[1]);

                var dt2 = new Date(dt.valueOf() - 1000);
                var temp = dt2.toTimeString().split(" ");
                var ts = temp[0].split(":");

                $timer.html(ts[1] + ":" + ts[2]);

                if ((ts[1] === "02") && (ts[2] === "00")) {
                    //modal alert
                    bootbox.confirm({
                        title: "Your session is about to expire due to inactivity",
                        message: "Would you like to continue your session?",
                        buttons: {
                            confirm: {
                                label: 'Yes',
                                className: 'btn-success'
                            },
                            cancel: {
                                label: 'No',
                                className: 'btn-danger'
                            }
                        },
                        callback: function (result) {
                            console.log('This was logged in the callback: ' + result);
                            if (result) location.reload();
                            else $('a[name="ucLiquidHeader_btnLogout"]')[0].click();
                        }
                    });
                }
                if ((ts[1] === "00") && (ts[2] === "00")) {
                    $('a[name="ucLiquidHeader_btnLogout"]')[0].click();
                }
                else { setTimeout(update, 1000); }
            }

    $("#navigation").slicknav({
        label:"Menu",
        prependTo: '#mobile_top_nav',
        allowParentLinks: true,
        removeClasses: true,
        removeIds: true,
        brand: ""
    });

    $(document).ready(function () {
        $(".header_search_expandbutton").click(function () {
            $(".header_searchbox_container").addClass("header_search_expanded");
            $(".header_searchbox").focus();
        });
        $(".header_search_closebutton").click(function () {
            $(".header_searchbox_container").removeClass("header_search_expanded");
        });

        $("#quicklinks_button").click(function () {
            $("#quicklinks_dropdown").toggle();
            $("#quicklinks_icon").toggleClass("quicklinks--icon__open");
        });

        $(document).click(function (e) {

            // Hide the dropdown if we click off of it
            if (!$(e.target).is("#quicklinks_button") && !$(e.target).parents().is("#quicklinks_button")) {
                $("#quicklinks_dropdown").hide();
                $("#quicklinks_icon").removeClass("quicklinks--icon__open");
            }

            // Hide the search bar if we click off of it
            if (!$(e.target).is(".header_searchbox_container") && !$(e.target).parents().is(".header_searchbox_container") && !$(e.target).is(".header_search_expandbutton") && !$(e.target).parents().is(".header_search_expandbutton")) {
                $(".header_searchbox_container").removeClass("header_search_expanded");
            }
        });
    });

    $(window).resize(function () {
        if ($(window).outerWidth() < 768) {
            $(".header_searchbox_container").removeClass("header_search_expanded");
        }
    });

input.script:
    $(document).ready(function () {
        var obj = $(".current_page");
        var searching = true;
        if (obj.length > 0) {
            while (searching) {
                obj = $(obj).parent();
                var arrow = $(obj).find("> a > .slicknav_arrow");
                if (arrow.length > 0) {
                    $(arrow).click();
                }
                if ($(obj).attr("class") == "slicknav_menu") {
                    searching = false;
                }
            }
        }
    });

    var hometext = $("#ucLeftNav_ctl00_lnkTopHome").text();

    //Slicknav
    $('#ul_dynamic_nav').slicknav({
        label: hometext,
        allowParentLinks: true,
        prependTo: "#dynamic_nav",
        removeIds: true,
        removeClasses: false,
        duplicate: false,
        closedSymbol: "",
        openedSymbol: ""
    });

link.script:
    $(document).ready(function () {
        // Liquid Button
        const liquidButton = $("#ucFooter_hlLiquidButton");
        const liquidHeader = $(".liquid-header");
        const body = $("body");

        liquidButton.click(function () {
            
            liquidButton.toggleClass("liquid-button__open");

            body.addClass("body__transition-liquid");

            if(liquidButton.hasClass("liquid-button__open")){
                body.addClass("body__liquid-open");
                liquidHeader.addClass("liquid-header__open");
            }
            else {
                body.removeClass("body__liquid-open");
                liquidHeader.removeClass("liquid-header__open");
            }

            setTimeout(function(){
                body.removeClass("body__transition-liquid");
            }, 300);
        });
    });
