    $(document).ready(function () {
        var obj = $(".current_page");
        var searching = true;
        if (obj.length > 0) {
            while (searching) {
                obj = $(obj).parent();
                var arrow = $(obj).find("> a > .slicknav_arrow");
                if (arrow.length > 0) {
                    $(arrow).click();
                }
                if ($(obj).attr("class") == "slicknav_menu") {
                    searching = false;
                }
            }
        }
    });
    var hometext = $("#ucLeftNav_ctl00_lnkTopHome").text();

    //Slicknav
    $('#ul_dynamic_nav').slicknav({
        label: hometext,
        allowParentLinks: true,
        prependTo: "#dynamic_nav",
        removeIds: true,
        removeClasses: false,
        duplicate: false,
        closedSymbol: "",
        openedSymbol: ""
    });

div.script:
var mobile = false;
function MobileChange() {
    if (window.innerWidth < 992 && !mobile) {
        $("#dynamic_nav").appendTo(".MobileDynamicNav");
        mobile = true;
    }
    if (window.innerWidth > 991 && mobile) {
        $("#dynamic_nav").appendTo("[id*=pnlLeftSide]");
        mobile = false;
    }
}
MobileChange();
$(window).resize(MobileChange);
