div.script:
$(document).ready(function () {
    $("#ef3e6695-2a70-44b1-9dd4-f0f320e32336").owlCarousel({
        navigation: false,
        pagination : false,
        autoPlay: getAutoPlayValue(),
        transitionStyle : "fade",
        items: '10',
        mouseDrag: false,
        singleItem: true});

    function getAutoPlayValue() {
        var autoPlayValue = 3000;
        if (autoPlayValue == 0) {
            autoPlayValue = false;
        }
        return 4 > 1 ? autoPlayValue : false;
    }
});

jQuery(document).ready(function () {
    jQuery('#131c7e47-5117-4e23-82c7-cc3281853c68').hide();
