        listOrder: 'desc',
        stateAbbr: '',
        viewAllText: 'View All',
        viewAllUrl: '/content/schools/ovca/en/calendar.html',
        viewAllTitle: 'View All',
        uuid: '131c7e47-5117-4e23-82c7-cc3281853c68',
        isEditMode: 'false',
        paginate: 'false',
        entriesPerPage: '10'
    });
});

