        window.resizeMiniCalendar20200429014621661 = function () {
        var calId = 'miniCal20200429014621661';
        var width = $('#' + calId).width();
        var calYearId = 'miniCalYear20200429014621661';
        var calMonthFullId = 'miniCalMonthFull20200429014621661';
        var calMonthShortId = 'miniCalMonthShort20200429014621661';
        var calGoToCalFullId = 'miniCalGotToCalFull20200429014621661';
        var calGoToCalShortId = 'miniCalGoToCalShort20200429014621661';
        if(width < 250) {
            $('#' + calYearId).hide();
            $('#' + calGoToCalFullId).hide();
            $('#' + calGoToCalShortId).show();
        }
        else {
            $('#' + calYearId).show();
            $('#' + calGoToCalFullId).show();
            $('#' + calGoToCalShortId).hide();
        }
        if(width < 170) {
                $('#' + calMonthFullId).hide();
                $('#' + calMonthShortId).show();                                       
        }
        else {
                $('#' + calMonthFullId).show();
                $('#' + calMonthShortId).hide();                                       
        }
    }               
}

