$(document).ready(function() { 
	$('a#sl-btn.button').click(function(e){ 
		$('#sidelinks ul').slideToggle("2000, easeInOutCubic").toggleClass('opened'); 
		var isVisible = $('#sidelinks .sl-list').is(".opened"); 

		if (isVisible === true ){
		   // Open
		   $(this).find($(".fa")).removeClass('fa-angle-double-down').addClass('fa-angle-double-up');
		}

		else {
		   // Close
		   $(this).find($(".fa")).removeClass('fa-angle-double-up').addClass('fa-angle-double-down');
		}

		e.preventDefault(); 
	});
});

