  new google.translate.TranslateElement({pageLanguage: 'en', includedLanguages: 'ar,en,es,fr,gu,pt,vi,ko,zh-CN', layout: google.translate.TranslateElement.InlineLayout.SIMPLE, autoDisplay: false}, 'google_translate_element');
}

