    function checkExternalLink()
    {
        var currentLink = document.getElementById("BannerButtonLink");
        currentLink.innerHTML = ""; 
        if (($(currentLink).has('img').length) === 0) { // && (!$(currentLink).hasClass('external'))
                if (currentLink.hostname !== location.hostname && (linkIsEnterpriseApproved(currentLink.href) === false)) {
                    $(currentLink).addClass('external');
                    $(currentLink).attr('target', '_blank');
                    $(currentLink).append("Opens In A New Window");
                    if ($(currentLink).hasClass('contentTile')) {
                        var currentP = $(currentLink).find('p');
                        $(currentP).append("");
                    }
                    else {
                        $(currentLink).append("");
                    }
                }
            }
    }
