div.script:
// Adobe Analytics event capture for Expand/Collapse Buttons //
$(document).ready(function(){
    var accordionBtns = document.querySelectorAll(".expand-collapse .item__label a");
    for (var i = 0; i < accordionBtns.length; i++){
        accordionBtns[i].addEventListener("click", function(e){
            var thisLinkName = e.target.innerText;
            console.log(thisLinkName);

            if (window.location.hostname === "about.att.com" && ddo) {
                var trackingInfo = {
                'linkName': thisLinkName,
                'linkDestinationURL': 'Expand/Collapse item toggled',
                'linkPosition': 'Expand/Collapse component'
                }
                ddo.pushEvent('linkClick', 'Link_Click', trackingInfo);
                console.log("Expand/Collapse toggled. Click sent to Adobe.");
            }
        });
    }
});

