var isiPad = navigator.userAgent.match(/iPad/i) != null;
if(!isiPad){
    $(window).scroll(function(){
        if($(window).scrollTop()<80){
            $(".leadingBar").css("position","absolute");
            $(".leadingBar").css("margin-top","-40px");
        }
        else if($(".leadingBar").css("position") == "absolute"){
            $(".leadingBar").css("position","fixed");
            $(".leadingBar").css("margin-top","-120px");
        }
    });
}

