    function GatherNGo(){
        var query=document.cse.q.value;
        document.cse.q.value="www.palmyraportal.org " + query;
        document.cse.submit();
    }
    function arrowIn(o){
        if(o.value=='search...') o.value='';
        $('#arrow').fadeIn(500);
        $('#arrow').css('display','inline');
    }
    function arrowOut(o){
        if(o.value==''){
            o.value='search...';
            $('#arrow').fadeOut(500);
            $('#arrow').css('display','none');
        }
    }

