html.script:
function confirmation(modulecode,schoolid,deptid){
 yn = confirm("Are you sure you want to permanently delete this module?");
 if (!yn) {
   alert("Module was not dropped.");
 } else {
   document.location = "deletemodule.php?mid=" + modulecode +"&schoolid="+schoolid+"&deptid="+deptid;
 }

}
