div.script:
(function() {
			var cx = '000954567685291728376:l4btouqacim';
			var gcse = document.createElement('script');
			gcse.type = 'text/javascript';
			gcse.async = true;
			gcse.src = 'https://cse.google.com/cse.js?cx=' + cx;
			var s = document.getElementsByTagName('script')[0];
			s.parentNode.insertBefore(gcse, s);
		})();

link.script:
	jQuery(function ($) {
		if(Cookies.get('TEXTSIZE')) { // look for a previous font size selection
			$('body').addClass(Cookies.get('TEXTSIZE'));
			$('.type-scale').find('button.' + Cookies.get('TEXTSIZE')).addClass('selected').attr('aria-current', 'true');
		}
		$('.type-scale button').click(function() { // change the font size based on a selection
			//$('.entry, .sidebar').matchHeight();
			var textSize = $(this).attr('class');
			$('.type-scale').find('button').removeClass('selected').removeAttr('aria-current');
			$(this).addClass('selected').attr('aria-current', 'true');
			$('body').removeClass('small medium large').addClass(textSize);
			Cookies.remove('TEXTSIZE'); // delete the old cookie & reset it
			Cookies.set('TEXTSIZE', textSize, { expires: 3 }); // sets cookie and it expires in 3 days
		});
		$('#contentskip').attr('tabindex','-1');
		$('#header').attr('tabindex','-2');
		$( "button.scroll" ).click(function() { // scroll button on full-screen homepage 
			$('html, body').animate({
				scrollTop: $(".entry").offset().top - 100 // account for fixed header
			}, 1000);
		});
		var toggled = false;
		$('button.menu-button').click(function() { // open up sitewide menu
			$("html, body").animate({ scrollTop: 0 });
			$('.sitewide-menu').slideToggle();
			$('.sitewide-menu').attr("tabindex",-1).focus();

			toggled = !toggled;
			$(this).attr("aria-expanded", toggled ? true : false);
			$('.flaticon-menu').text(toggled ? 'Menu expanded' : 'Menu collapsed');
		});
		$('button.search-button').click(function() { // open up sitewide search
			$("html, body").animate({ scrollTop: 0 });
			$('.sitewide-search').slideToggle();
			$('#gsc-i-id1').focus();
		});
		$('button.return-menu').click(function() { // display main menu on office sites
			$(this).fadeOut();
			$('.return').find('img.logo').show();
			$('.return').find('ul.menu').show();
		});
		$('ul.menu li').hover(function() { // basic drop-down functionality
    		$(this).children('.sub-menu').show();
    		$(this).addClass('selected');
			}, function() {
			$(this).children('.sub-menu').hide();
			$(this).removeClass('selected');
		});
		$('.mobile-nav .menu-item a').focus(function() {
			$(this).parent().find('.sub-menu').eq(0).show();
		});
		$('.menu-item').attr('role', 'menuitem');
		$('.background').imgLiquid({
			verticalAlign: '20%'
		}); // fill image container 
		$('.feature').fitVids();
		$('.navy, .green, .grey, .background .content').find('h1, h2, h3, h4, h5, h6').addClass('light'); // look for all headings in dark boxes
		$('.gold').find('h1, h2, h3, h4, h5, h6').addClass('dark'); // look for all headings in gold boxes
		$(".sidebar, .box").each(function() { // look for icons in the sidebar and add numbers
			$(this).find(".icon.default").each(function(index) {
				$(this).find('.icon-graphic').prepend("" + (index + 1) + "");
			})
		});
		//$(".icon").click(function() { //set entire .icon box to be clickable
		//	window.location = $(this).find("a").attr("href"); 
		//	return false;
		//});
		$("#footer ul.menu li.sign a, .languages ul.menu li.sign a").prepend('');
		//$('.sidebar .icon').append('' + index(this) + '');
	});



	jQuery(function ($) {
		$('.entry, .sidebar').matchHeight();
		$( window ).resize(function() { // functions to run on resize
			$('.entry, .sidebar').matchHeight();
		});
		$('.type-scale button').click(function() {
			$('.entry, .sidebar').matchHeight();
		});
	});



	jQuery(function ($) {
		$('.bxslider').bxSlider({
			ariaLive: false,
			ariaHidden: false,
			mode: 'horizontal',
			pager: true,
			auto: true, 
			autoHover: true,
			controls: false,
			autoControls: true,
			autoControlsCombine: true,
			touchEnabled: false,
			slideSelector: 'div.inner'
		});
		
		$('.bxslider-new').bxSlider({
			mode: 'fade',
			pager: false,
			auto: true, 
			autoHover: false,
			controls: false,
			autoControls: true,
			touchEnabled: false,
			slideSelector: 'li'
		});
		//Moved Start/stop button
		$('.bx-controls-auto').appendTo('.bx-default-pager');
		//Hide the cloned sliders;
		// $('.bxslider .inner').attr('aria-hidden', 'false');
		$('.bx-clone').attr('aria-hidden', 'false');
		$('.bx-clone a').attr('tabindex', -1);
	});
