var esFiltered = false;

$('.sel-es').on('click', function(){
  if (esFiltered === false) {
    $('.school-slider').slick('slickFilter','.es');
    $(this).css('background','#C2E2DC');
    $(this).siblings().css('background','none');
    esFiltered = true;
  } else {
    $('.school-slider').slick('slickUnfilter');
    $(this).css('background','none');
    esFiltered = false;
  }
});

var msFiltered = false;

