$(function(){
	$('#slides').slides({				
		preload: true, 
		generateNextPrev: false, 
		pagination: true, 
		generatePagination: true, 
		paginationClass: 'pagination', 
		fadeSpeed: 1000, 
		effect: 'slide', 
		crossfade: true, 
		play: 5000, 
		pause: 2500, 
		hoverPause: true, 
		autoHeight: false, 
		bigTarget: false 
	});
});

