link.script:
var sqb;
$(document).ready(function() {
    var searchQueryBox = $("#searchQuery");
    sqb = searchQueryBox;
    if (searchQueryBox) {
        searchQueryBox.keypress(
            function(e) {
                console.log(e);
                var code = e.keyCode || e.which;
                if (code == 13) {
                    e.preventDefault();
                    e.cancelBubble = true;
                    e.returnValue = false;
                    return false;
                }
                return true;
            });
    }
})

