		                "imageNum480"           : 1, //NUMBER OF IMAGES TO DISPLAY AT 480
		                "imageNum320"           : 1, //NUMBER OF IMAGES TO DISPLAY AT 320
		                "backIconFontLetter"    : "", //LETTER OR NUMBER TO USE FOR THE ICON FONT FOR THE BACK BUTTON
		                "nextIconFontLetter"    : "", //LETTER OR NUMBER TO USE FOR THE ICON FONT FOR THE BACK BUTTON
		                "buttonColor"           : "#FFF", //COLOR TO BE USED FOR THE BACK AND NEXT BUTTONS
		                "extraBackIconClass"    : "icon-font-arrow-left",
		                "extraNextIconClass"    : "icon-font-arrow-right",
		                "controlGutter"         : "88" //LEFT/RIGHT MARGINS TO ALLOW ROOM FOR CONTROLS
		        });
		}

        $("#hp-content-lower .headlines-scroll-ctrl").removeClass("icon-font");
	});	

	function pgScreenSize(){
		var getWidth = $(window).width();
		if (getWidth >= 1024) { // 960+
			photoGallery();
		} if (getWidth >= 768 && getWidth < 1024) { // 768+
			photoGallery();
			appView();
		} if (getWidth >= 640 && getWidth < 768) { // 640+
			photoGallery();
			appView();
		} if (getWidth >= 480 && getWidth < 640) { // 480+
			photoGallery();
            appView();
		} if (getWidth >= 320 && getWidth < 480) { // 320+
			photoGallery();
            appView();
		}
	}

	function photoGallery(){
		if(!$("#sw-content-container10.region.ui-hp .rs-gallery-container").length){
		        // MULTIMEDIA ROTATOR
		        $("#sw-content-container10.ui-hp div.ui-widget.app.cs-rs-multimedia-rotator").csRsMultimediaRotator({
