										$('#hp-slideshow .rs-gallery-container .rs-gallery-desc').css("top",galleryDiff);
									} else { // 768+
										$('#hp-slideshow .rs-gallery-container .rs-gallery-desc').css("top",galleryDiffMobile);
									}		
								});
		                },
		                "allImagesLoaded"         : function() { },
		                "onThumbnailLoad"         : function() { },
		                "allThumbnailsLoaded"     : function() { },
		                "onTransitionStart"       : function() { },
		                "onTransitionEnd"         : function() { },
		                "watchVidClick"           : function() { },
		                "closeVidClick"           : function() { },
		                "allLoaded"               : function() { 
		                				var getWidth = $(window).width();
		                				var pageHeight = $(window).height(); 
		                				var galleryHeight = $("#hp-slideshow").height();
		                				var galleryDescHeight = $('#hp-slideshow .rs-gallery-container .rs-gallery-desc').height();
		                				galleryDiff = (pageHeight - galleryDescHeight)/2;
		                				galleryDiffMobile = (galleryHeight - galleryDescHeight)/2;
		                				if (getWidth >= 1024) { // 960+
									$('#hp-slideshow .rs-gallery-container .rs-gallery-desc').css("top",galleryDiff);
								} else { // 768+
									$('#hp-slideshow .rs-gallery-container .rs-gallery-desc').css("top",galleryDiffMobile);
								}
		                				$(window).resize(function(){
									var getWidth = $(window).width();
			                				var pageHeight = $(window).height(); 
			                				var galleryHeight = $("#hp-slideshow").height();
			                				var galleryDescHeight = $('#hp-slideshow .rs-gallery-container .rs-gallery-desc').height();
