    function stickyCalendarFix() {
    	var headerHeight = $("#gb-mystart-outer").height();

        if ($(window).scrollTop() <= headerHeight) {

            if($(".sp").length && $(".ui-widget.app.calendar").length) {
                $(".wcm-controls").css("margin-top", "0px");
            }
        } else {

            if($(".sp").length && $(".ui-widget.app.calendar").length) {
                if($(".wcm-controls").hasClass("wcm-stuck")) {
                    $(".wcm-controls").css("margin-top", headerHeight + "px");
                }
            }
        }
    }

	function searchText() {
		//DOC custom mystart search placeholder
	        //SEARCH TEXT
	        $("div#sw-mystart-search input#sw-search-input").removeAttr("placeholder");
		$("div#sw-mystart-search input#sw-search-input").attr("value", "Search Our Site");
		$("div#sw-mystart-search input#sw-search-input").focus(function() {
			if($(this).val() == "Search Our Site") {
				$(this).val("");
			}
		});
		$("div#sw-mystart-search input#sw-search-input").blur(function() {
			if($(this).val() == "") {
				$(this).val("Search Our Site");
			}
		});
	}

	function eventsByDay(container) {
		//DOC move all events with the same dates into the same container
		var countLi = 0;
		$(".ui-article", container).each(function(){
			if (!$(this).find("h1.ui-article-title.sw-calendar-block-date").size()){
				var moveArticle = $(this).html();
				$(this).parent().prev().children().children().next().append(moveArticle);
				$(this).parent().remove();

			};
		});
	}

	function calendarListView(){
		//DOC auto click function for schoolwires calendar list view for small mobile devices
		if(!$("a.ui-btn-toggle.list").hasClass("active")){
			$("a.ui-btn-toggle.list").click();
		}
	}

	function socialIcons(){
		// SET UP THE CHECK VARIABLES
		var hasIcons = 0;
		var twitterCheck = 'true';
		var facebookCheck = 'true';
		var pinterestCheck = 'false';

		// SHOW THE ICONS THAT ARE NEEDED
	    	if(twitterCheck == "true"){
		        var twitterTarget = 'New Window';
		        if(twitterTarget == "New Window"){
		        	twitterTarget = "_blank";
		        } else {
		        	twitterTarget = "_self";
		        }
		        $(".icn.twitter").removeClass("hidden").attr("target", twitterTarget);
		        hasIcons = 1;
		}
		if(facebookCheck == "true"){
		        var facebookTarget = 'New Window';
		        if(facebookTarget == "New Window"){
		        	facebookTarget = "_blank";
		        } else {
		        	facebookTarget = "_self";
		        }
		        $(".icn.facebook").removeClass("hidden").attr("target", facebookTarget);
			hasIcons = 1;
		}
		if(pinterestCheck == "true"){
		        var pinterestTarget = 'New Window';
		        if(pinterestTarget == "New Window"){
		        	pinterestTarget = "_blank";
		        } else {
		        	pinterestTarget = "_self";
		        }
		        $(".icn.pinterest").removeClass("hidden").attr("target", pinterestTarget);
		        hasIcons = 1;
		}
	}

    function accessibility(){
    	$("#gb-mystart-inner .sw-mystart-dropdown.schoollist").removeAttr("tabindex").removeAttr("aria-label");
    	$("#gb-mystart-inner .sw-mystart-dropdown.schoollist .selector").removeAttr("aria-hidden")
        	.attr({
            	"aria-expanded": "false",
                "role": "button",
                "aria-label": "Select a School",
                "tabindex": "0",
                "aria-haspopup": "true"
            });

        $("#gb-mystart-inner #sw-myaccount")
        	.attr({
            	"aria-expanded": "false",
                "role": "button",
                "aria-label": "User Options",
                "aria-haspopup": "true"
            }); 

            $(".sw-mystart-dropdown .selector").click(function(){
            	// IF THE DROPDOWN IS OPEN, CLOSE IT
                        if($(this).parent().hasClass("open")){
                        	$(this).parent().find(".sw-dropdown .sw-dropdown-list a",this).attr("tabindex", "-1");
                            $(this).attr("aria-expanded", "false").parent().removeClass("open").find(".sw-dropdown",this).attr("aria-hidden", "true").slideUp(300, "swing"); 
                        } else {
                        	$(this).attr("aria-expanded", "true").parent().addClass("open").find(".sw-dropdown",this).attr("aria-hidden", "false").slideDown(300, "swing", function(){
                                $(this).parent().find(".sw-dropdown .sw-dropdown-list li:first-child a", this).attr("tabindex", "0").focus();
                            });
                        }
            });

            $("#sw-myaccount").click(function(){
                // IF THE DROPDOWN IS OPEN, CLOSE IT
                    if($(this).parent().hasClass("open")){
                        $(this).parent().find(".sw-dropdown a",this).attr("tabindex", "-1");
                        $(this).attr("aria-expanded", "false").parent().removeClass("open").find(".sw-dropdown",this).attr("aria-hidden", "true").slideUp(300, "swing"); 
                    } else {
                        $(this).attr("aria-expanded", "true").parent().addClass("open").find(".sw-dropdown",this).attr("aria-hidden", "false").slideDown(300, "swing", function(){
                            $(this).parent().find(".sw-dropdown li:first-child a", this).attr("tabindex", "0").focus();
                        });
                    }
            });

        // MYSTART DROPDOWN SELECTOR KEYDOWN EVENTS
        	var _this = this;
            $(".sw-mystart-dropdown .selector").keydown(function(e) {

                // CAPTURE KEY CODE
                switch(e.keyCode) {
                    // CONSUME LEFT AND UP ARROWS
                    case _this.csKeyCodes.enter:
                    case _this.csKeyCodes.space:
                        e.preventDefault();

                        // IF THE DROPDOWN IS OPEN, CLOSE IT
                        if($(this).parent().hasClass("open")){
                        	$(this).parent().find(".sw-dropdown .sw-dropdown-list a",this).attr("tabindex", "-1");
                            $(this).attr("aria-expanded", "false").parent().removeClass("open").find(".sw-dropdown",this).attr("aria-hidden", "true").slideUp(300, "swing"); 
                        } else {
                        	$(this).attr("aria-expanded", "true").parent().addClass("open").find(".sw-dropdown",this).attr("aria-hidden", "false").slideDown(300, "swing", function(){
                                $(this).parent().find(".sw-dropdown .sw-dropdown-list li:first-child a", this).attr("tabindex", "0").focus();
                            });
                        }
                    break;

                    // CONSUME TAB KEY
                    case _this.csKeyCodes.tab:
                    	if($(this).parent().find(".sw-dropdown .sw-dropdown-list a",this).length) {
                        	$(this).parent().find(".sw-dropdown .sw-dropdown-list a",this).attr("tabindex", "-1");
                        	$(this).attr("aria-expanded", "false").parent().removeClass("open").find(".sw-dropdown",this).attr("aria-hidden", "true").slideUp(300, "swing");
                        }
                    break;

                    // CONSUME LEFT AND UP ARROWS
                    case _this.csKeyCodes.down:
                    case _this.csKeyCodes.right:
                        e.preventDefault();

                        $(this).parent().find(".sw-dropdown .sw-dropdown-list a",this).attr("tabindex", "-1");
                        $(this).parent().find(".sw-dropdown .sw-dropdown-list li:first-child > a", this).attr("tabindex", "0").focus();
                    break;
                }
            }); 

            $(".sw-mystart-dropdown.translate .sw-dropdown-list li a").keydown(function(e) {
                  // CAPTURE KEY CODE
                  switch(e.keyCode) {
                      // CONSUME LEFT AND UP ARROWS
                      case csKeyCodes.left:
                      case csKeyCodes.up:
                          e.preventDefault();

                          // IS FIRST ITEM
                          if($(this).parent().is(":first-child")) {
                              // FOCUS LAST ITEM
                              $(this).closest(".sw-dropdown-list").find("li:last-child > a").focus();
                          } else {
                              // FOCUS PREVIOUS ITEM
                              $(this).parent().prev("li").find("> a").focus();
                          }
                      break;

                      // CONSUME RIGHT AND DOWN ARROWS
                      case csKeyCodes.right:
                      case csKeyCodes.down:
                          e.preventDefault();
                          e.stopImmediatePropagation();

                          // IS LAST ITEM
                          if($(this).parent().is(":last-child")) {
                              // FOCUS FIRST ITEM
                              $(this).closest(".sw-dropdown-list").find("li:first-child > a").focus();
                          } else {
                              // FOCUS NEXT ITEM
                              $(this).parent().next("li").find("> a").focus();
                          }
                      break;

                      // CONSUME TAB KEY
                      case csKeyCodes.tab:
                          e.preventDefault();

                          $(this).parent().parent().parent().parent().find(".sw-dropdown .sw-dropdown-list a",this).attr("tabindex", "-1");
                          $(this).parent().parent().parent().parent().find(".selector",this).attr("aria-expanded", "false").parent().removeClass("open").find(".sw-dropdown",this).attr("aria-hidden", "true").slideUp(300, "swing"); 
						  $(".sw-mystart-dropdown.translate .selector").focus();

                      break;

                      // CONSUME HOME KEY
                      case csKeyCodes.home:
                          e.preventDefault();

                          // FOCUS FIRST ITEM
                          $(this).closest(".sw-dropdown-list").find("li:first-child > a").focus();
                      break;

                      // CONSUME END KEY
                      case csKeyCodes.end:
                          e.preventDefault();

                          // FOCUS LAST ITEM
                          $(this).closest(".sw-dropdown-list").find("li:last-child > a").focus();
                      break;
                  }
              });

            $("#sw-myaccount").keydown(function(e) {

                // CAPTURE KEY CODE
                switch(e.keyCode) {
                    // CONSUME LEFT AND UP ARROWS
                    case _this.csKeyCodes.enter:
                    case _this.csKeyCodes.space:
                        e.preventDefault();

                        // IF THE DROPDOWN IS OPEN, CLOSE IT
                        if($(this).parent().hasClass("open")){
                        	$(this).parent().find(".sw-dropdown a",this).attr("tabindex", "-1");
                            $(this).attr("aria-expanded", "false").parent().removeClass("open").find(".sw-dropdown",this).attr("aria-hidden", "true").slideUp(300, "swing"); 
                        } else {
                        	$(this).attr("aria-expanded", "true").parent().addClass("open").find(".sw-dropdown",this).attr("aria-hidden", "false").slideDown(300, "swing", function(){
                                $(this).parent().find(".sw-dropdown li:first-child a", this).attr("tabindex", "0").focus();
                            });
                        }
                    break;

                    // CONSUME TAB KEY
                    case _this.csKeyCodes.tab:
                    	if($(this).parent().find(".sw-dropdown a",this).length) {
                        	$(this).parent().find(".sw-dropdown a",this).attr("tabindex", "-1");
                        	$(this).attr("aria-expanded", "false").parent().removeClass("open").find(".sw-dropdown",this).attr("aria-hidden", "true").slideUp(300, "swing");
                        }
                    break;

                    // CONSUME LEFT AND UP ARROWS
                    case _this.csKeyCodes.down:
                    case _this.csKeyCodes.right:
                        e.preventDefault();

                        $(this).parent().find(".sw-dropdown a",this).attr("tabindex", "-1");
                        $(this).parent().find(".sw-dropdown li:first-child > a", this).attr("tabindex", "0").focus();
                    break;
                }
            }); 

            $("#sw-myaccount-list li a").keydown(function(e) {
                  // CAPTURE KEY CODE
                  switch(e.keyCode) {
                      // CONSUME LEFT AND UP ARROWS
                      case csKeyCodes.left:
                      case csKeyCodes.up:
                          e.preventDefault();

                          // IS FIRST ITEM
                          if($(this).parent().is(":first-child")) {
                              // FOCUS LAST ITEM
                              $(this).closest("#sw-myaccount-list").find("li:last-child > a").focus();
                          } else {
                              // FOCUS PREVIOUS ITEM
                              $(this).parent().prev("li").find("> a").focus();
                          }
                      break;

                      // CONSUME RIGHT AND DOWN ARROWS
                      case csKeyCodes.right:
                      case csKeyCodes.down:
                          e.preventDefault();
                          e.stopImmediatePropagation();

                          // IS LAST ITEM
                          if($(this).parent().is(":last-child")) {
                              // FOCUS FIRST ITEM
                              $(this).closest("#sw-myaccount-list").find("li:first-child > a").focus();
                          } else {
                              // FOCUS NEXT ITEM
                              $(this).parent().next("li").find("> a").focus();
                          }
                      break;

                      //CONSUME TAB KEY
                      case csKeyCodes.tab:
                          e.preventDefault();

                            $(this).parent().parent().parent().find(".sw-dropdown a",this).attr("tabindex", "-1");
                            $(this).parent().parent().attr("aria-expanded", "false").parent().removeClass("open clicked-state").find(".sw-dropdown",this).attr("aria-hidden", "true").slideUp(300, "swing"); 
                            $("#sw-myaccount").attr("aria-expanded","false").focus();

                      break;

                      // CONSUME HOME KEY
                      case csKeyCodes.home:
                          e.preventDefault();

                          // FOCUS FIRST ITEM
                          $(this).closest("#sw-myaccount-list").find("li:first-child > a").focus();
                      break;

                      // CONSUME END KEY
                      case csKeyCodes.end:
                          e.preventDefault();

                          // FOCUS LAST ITEM
                          $(this).closest("#sw-myaccount-list").find("li:last-child > a").focus();
                      break;
                  }
              });

    }


	var parallaxScroll = function(){
		if ($(window).scrollTop() <= 40) {
			$("#gb-channel-list").addClass("channel-drop-up");
		} else {
			$("#gb-channel-list").removeClass("channel-drop-up");
		}
	}

