    function addFramesetTranslate() {
		var pageURL = window.location.href;
		pageURL = encodeURIComponent(pageURL);
		var translateDropdown = '
' +
									'
' +
                                        '
' +
                                            //'
Translate this page...' +
                                            '' +
                                                '
' +
                                                    'English to Spanish' +
                                                '' +
                                                '
' +
                                                    'English to Arabic' +
                                                '' +
                                                '
' +
                                                    'English to Chinese (Simplified)' +
                                                '' +
                                                '
' +
                                                    'English to Chinese (Traditional)' +
                                                '' +
                                                '
' +
                                                    'English to French' +
                                                '' +
                                                '
' +
                                                    'English to German' +
                                                '' +
                                                '
' +
                                                    'English to Italian' +
                                                '' +
                                                '
' +
                                                    'English to Japanese' +
                                                '' +
                                                '
' +
                                                    'English to Korean' +
                                                '' +
                                                '
' +
                                                    'English to Portuguese' +
                                                '' +
                                                '
' +
                                                    'English to Russian' +
                                                '' +
                                            '' +
                                        '' +
                                    '';
		var pageHost = window.location.host;
		if(pageHost != "translate.googleusercontent.com") {				
			$(translateDropdown).appendTo('#gb-translate');
			$(".translate").click(function() {
				$(".sw-dropdown",this).show();
                $(this).toggleClass('open');
                if($(".translate").hasClass("open")) {
                	// FOCUS THE FIRST LINK
                	$(".sw-dropdown-list > li:first-child > a").focus();

               		// ADJUST ARIA ATTRIBUTES
                	$(".sw-dropdown").attr("aria-hidden", "false");
            	} else {
                	// ADJUST ARIA ATTRIBUTES
                	$(".sw-dropdown").attr("aria-hidden", "true");
            	}
			});

             $(".translate").hover(function(e) {
				e.preventDefault();
			}, function() {
				$(".sw-dropdown",this).slideUp(300, "swing");
                if($(".translate").hasClass("open")) {
                    // FOCUS THE FIRST LINK
                    $(".sw-dropdown-list > li:first-child > a").focus();

                    // ADJUST ARIA ATTRIBUTES
                    $(".sw-dropdown").attr("aria-hidden", "false");
            	} else {
                    // ADJUST ARIA ATTRIBUTES
                    $(".sw-dropdown").attr("aria-hidden", "true");
                }
			});
		}
    }

