div.script:
//event listener: DOM ready
function addLoadEvent(func) {
    var oldonload = window.onload;
    if (typeof window.onload != 'function') {
        window.onload = func;
    } else {
        window.onload = function () {
            if (oldonload) {
                oldonload();
            }
            func();
        }
    }
}
//call plugin function after DOM ready
addLoadEvent(function () {
    outdatedBrowser({
        bgColor: '#f25648',
        color: '#ffffff',
        lowerThan: 'boxShadow',
        languagePath: ''
    })
});

$(function() {
$(".filtered-faq").hide();
//$("#accordion").clone().appendTo($(".filtered-faq"));

$("#covid19-faq-filter").keyup(function() {
var search = $("#covid19-faq-filter").val().toUpperCase();

if (search.length > 0)
{
console.log("searching...");
//$('.unfiltered-faq').fadeOut();
//$(".filtered-faq").fadeIn();
$("#accordion > .panel.panel-default").each(function() {
var q = $(this).find(".question").text().toUpperCase();
var a = $(this).find(".answer").text().toUpperCase();
if (q.indexOf(search) > -1 | a.indexOf(search) > -1) {
$(this).fadeIn();
} else {
$(this).fadeOut();
}
});
} else{
//$(".filtered-faq").fadeOut();
//$('.unfiltered-faq').fadeIn();
$("#accordion > .panel.panel-default").each(function() {
    $(this).fadeIn();
})
}
})
});

