jQuery('document').ready(function($){
      $(".ui-accordion-header a").attr("href","#");
      $("td").removeAttr("width");
     $("#contentWrapper #content").attr('id', 'content2');
     $("#content iframe").removeAttr('frameborder');

     $('#navbar-main a, .box-secondary_menu a').filter(function(i) {
        return ( $(this).text() === 'Schools' || $(this).text() === 'For Staff' || $(this).text() === 'Newsroom' || $(this).text() === 'Calendars');
     }).each(function(i) {
        if ($(this).text() === 'Schools')
          $(this).attr('href','/find-schools'); 
        if ($(this).text() === 'For Staff')
          $(this).attr('href','/content/staff');
        if ($(this).text() === 'Newsroom')  
          $(this).attr('href','/content/newsroom'); 
        if ($(this).text() === 'Calendars')
          $(this).attr('href','/calendars');   
     });  

     $('.radix-layouts-sidebar a').filter(function(i) {
        return $(this).text() === 'Overview';
     }).each(function(i) {
        var label = $(this).attr('href').replace('/content/');
        $(this).attr('aria-label', label);   
     }); 

     $('.timed-content').each(function() {
     let startdate = Date.parse($(this).data('startdate'));
    let enddate = Date.parse($(this).data('enddate'));
    let currentdate = Date.now();


    $(this).hide();

    if (startdate <= currentdate && enddate >= currentdate) {
      $(this).show();
    }
  });

});

      jQuery(document).ready(function($) {
       var getYTVideoCode = function(url) {
          let newval = "";
          let code = "";
          let found = url.match(/(youtu\.?be)/) || [];
          if (found.length) {
            if ((newval = url.match(/(\?|&)v=([^&#]+)/))) {
              code = newval.pop();
            } else if ((newval = url.match(/(\.be\/)+([^\/]+)/))) {
              code = newval.pop();
            } else if ((newval = url.match(/(\embed\/)+([^\/]+)/))) {
              code = newval.pop().replace("?rel=0", "");
            }
          }
  
  		  code = code.replace(/\?.*/,"");	

          return code;
        };

        $("#content iframe").each(function(i) {
          var embed_link = $(this).attr("src");
          var video_code = getYTVideoCode(embed_link);
          console.log(video_code);
          var currentiframe = this;
          if (video_code.length) {
            $.ajax({
              type: "GET",
              url: "https://video.google.com/timedtext",
              data: { v: video_code, lang: "en" },
              dataType: "xml",
              success: function(xml) {
                if (xml) {
                  $(currentiframe)
                    .parent()
                    .after(
                      "
View transcript for this video"
                    );
                }
              }
            });
          }
        });
      });

jQuery(document).ready(function($) {
  $('#search-block-form input:first').attr("placeholder","");
  $('#search-block-form').submit(function(e) { 
    e.preventDefault(); 
    var term = $('#search-block-form input:first').val();
    window.location = '/content/search?gs=' + term;
  });

  if($('#searchterm').length) {
    var searchterm = location.search.replace("?gs=","");
    $("#searchterm").text(decodeURIComponent(searchterm));
  }
});
