	function imgResize(){
		var imgWidth = $("#hp-slideshow .rotating-images").width();
		var initW = 535, initH = 370;
		var ratio = initH / initW;
		var imgHeight = imgWidth * ratio;
		$("#hp-slideshow .rotating-images, #hp-slideshow .rotating-images ul.pictures li").css("height", imgHeight);
	}

	function modEvents(){
