// Brenton Kelly - Case: 04134415
$(window).load(function() {
    setTimeout(function() {
        $("#gb-channel-list .sw-channel-item.mega-channel").each(function() {
            $(".sw-channel-dropdown", this).css("width", "1024px");
            setDropdownPosition($(this));
        });
        $(window).resize(function(){
            if(csGetBreakPoint() == "desktop") {
                $("#gb-channel-list .sw-channel-item.mega-channel").each(function(){
                    setDropdownPosition($(this));
                });
            }
        });
        function setDropdownPosition(element) {
            var dashboardIsPresent = $("#dashboard-sidebar").length;
            var windowWidth = (dashboardIsPresent) ? $(window).width() - 50 : $(window).width();
            var position = $(element).offset();
            var leftPos = position.left;
            var dropdownWidth = $(".sw-channel-dropdown", element).outerWidth(true);
            var isOverflowing = leftPos + dropdownWidth;
            var offset = (dashboardIsPresent) ? (isOverflowing - windowWidth) - 50 : (isOverflowing - windowWidth);

            if(isOverflowing >= windowWidth){
                $(".sw-channel-dropdown", element).css({"width": ((windowWidth < 1024) ? windowWidth : "1024px"), "max-width": "1024px", "left": -offset});
            } else {
                $(".sw-channel-dropdown", element).css({"width": ((windowWidth < 1024) ? windowWidth : "1024px"), "left": "0px"});
            }

        }
    }, 100);
});

