	var csKeyCodes = { "tab": 9, "enter": 13, "esc": 27, "space": 32, "end": 35, "home": 36, "left": 37, "up": 38, "right": 39, "down": 40 };
    var csSlideshowDescFixed = false;

	$(window).load(function() {
    	$(".global-icon").removeAttr("id");
        $(".global-icon.custom img").attr("alt","");
        $("#gb-header-right").fadeIn("fast");

        calendarListView();
		searchText();
        sameHeights();

        //ACCESSIBLE NAV APP
        $(".ui-widget.app.navigation").csAccessibleNavigationApp();

		//HIDE EMPTY DROPDOWN THAT GETS PLACED BY THE SYSTEM IF CHANNEL HAS NO SECTIONS BUT CHANNEL HOMEPAGE IS TURNED ON
		$("ul.sw-channel-dropdown").each(function() {
			var systemDropdown = $(this);
			if(!$("li",systemDropdown).length) {
				$(systemDropdown).remove();
			}
		});

        //DOC ADD HAS-DROPDOWN CLASS TO CHANNELS THAT HAVE A SECTION DROPDOWN
        $("li.sw-channel-item").each(function() {
			if($(".sw-channel-dropdown", this).length) {
				$(this).addClass("has-dropdown");
			}
		});

        //ADD SITE SHORTCUTS FROM MEGA MENU
        if($(window).width() < 1024) {
          if($(".rs-menu-option.additional-school-resources .rs-menu-option-sublist li").length < 3) {
            $("#gb-schoollist-menu #gb-school-list-panels-inner .schoollist-extra-links .ui-widget.app.navigation .ui-widget-detail .site-shortcuts li").each(function() {
                var resourceLinks = this;
                $(resourceLinks).clone().appendTo(".rs-menu-option.additional-school-resources .rs-menu-option-sublist");
            });
          }
        }
        $("#gb-header-right").fadeIn();

        $('.gb.footer.two li').each(function(){
            if( $.trim($(this).text()) == '')
                $(this).remove();
        });

        $('.cs-mystart-dropdown.translate').mouseleave(function(e) {
                            if(e.target.nodeName.toLowerCase() !== 'select') {
                                $('.cs-dropdown', this).hide();
                            }
                        });

        $("#sw-maincontent").css({
                            "display": "block",
                            "position": "relative",
                            "top": "-" + ($("#gb-channel-list-outer").outerHeight(true) + $("#gb-sticky-header-outer").outerHeight(true) ) + "px"
                        });

        megaMenuDropdown();

	});

    $(window).scroll(function() {
    	if($(window).width() >= 1024) {
        	var scrolled = $(window).scrollTop();
            //var mystartHeight = $("#gb-mystart-outer").height();
            var mystartHeight = 1;
            if(scrolled > mystartHeight) {
            	$("#gb-channel-list-outer").addClass("fixed");
                $("#hp-slideshow-outer").css("margin-top","55px");
            } else {
            	$("#gb-channel-list-outer").removeClass("fixed");
                $("#hp-slideshow-outer").css("margin-top","0");
            }

          if($(".hp").length) {
              if(scrolled > 1) {
                  $("#gb-channel-list-outer").addClass("fixed");
                  //$("#gb-sticky-icons").css("right","0");
                  $("#gb-header-outer").fadeOut();
                  $("#gb-sticky-header-outer").slideDown(function() {
                    if($("#hp-content-outer").hasClass("small-gallery")) {
                      $("#hp-content-outer").css("margin-top","140px");
                    }
                  });
                  var channelHeight = $("#gb-channel-list-outer").height();
                  if(channelHeight > 55) {
                    $("#gb-sticky-header-outer").css("top",channelHeight);
                    $("#gb-sticky-icons").css("top","190px");
                  }

              } else {
                  $("#gb-channel-list-outer").removeClass("fixed");
                  //$("#gb-sticky-icons").css("right","-40px");
                  $("#gb-sticky-header-outer").slideUp();
                  $("#gb-header-outer").fadeIn();
                  if($("#hp-content-outer").hasClass("small-gallery")) {
                    $("#hp-content-outer").css("margin-top","0");
                  }
              }
          }

          if($(".sp").length || $(".spn").length) {
              if(scrolled > 1) {
                  $("#gb-channel-list-outer").addClass("fixed");
                  //$("#gb-sticky-icons").css("right","0");
                  $("#gb-header-outer").css("margin-top","55px");
                  $("#gb-header").fadeOut();
                  $("#gb-header-outer").css("border","0 none");
                  $("#gb-sticky-header-outer").slideDown().css("top",$("#gb-channel-list-outer").outerHeight());

                  if($(".ui-widget.app.calendar").length) {
                    if($(".wcm-controls").hasClass("wcm-stuck")) {
                        $(".wcm-controls").css("margin-top", $("#gb-channel-list-outer").outerHeight() + $("#gb-sticky-header-outer").outerHeight());
                    }
                  }
              } else {
                  $("#gb-channel-list-outer").removeClass("fixed");
                  //$("#gb-sticky-icons").css("right","-40px");
                  $("#gb-header-outer").css("margin-top","0");
                  $("#gb-header").fadeIn();
                  $("#gb-header-outer").css("border","1px solid rgba(0,0,0,.15)");
                  $("#gb-sticky-header-outer").slideUp();
              }
          }
        }
    });

