    function modChannelBar() {
    	// ********** CHANNEL BAR ********** //
		//DOC add first class to first channel item
		$("li.sw-channel-item").first().addClass("first");

		//DOC add last class to last channel item
		$("li.sw-channel-item").last().addClass("last");

        //CHANNEL ICONS
        $("
\
          	\
            	\
                Parent Portal\
            \
          \

\
          	\
            	\
                Search\
            \
          \

\

\

Translate\

\
                    \
                \
            \
          \

\

\

User Options\

\
                    \
                \
            \
          \
        ").prependTo(".sw-channel-list");
        $("#cs-search-input-cont").appendTo("#channel-navigation");
        $("X").appendTo("#cs-search-input-cont");
        $("ul.sw-channel-list li.sw-channel-item.channel-icon").fadeIn("fast");
        //SEARCH
        $("li.sw-channel-item.cs-search-icon a").click(function(e) {
            e.preventDefault();
            if($("li.sw-channel-item.cs-search-icon").hasClass('open')) {
            	$("#cs-search-input-cont").removeClass('open').attr({
                    "aria-expanded": "false"
                });

                $("li.sw-channel-item.cs-search-icon").delay(100).animate({
                    "margin-left": "0"
                }, "fast", function() {
                    $(this).attr({
                        'aria-expanded': 'false'
                    });

                }).removeClass("open");
            } else {
            	//$("li.sw-channel-item.cs-search-icon").addClass("open");
                $("li.sw-channel-item.cs-search-icon").animate({
                	"margin-left": "-115px"
                }, "fast", function() {
                	$(this).attr({
                        'aria-expanded': 'true'
                    });
                    $("#cs-search-input-cont").addClass('open').attr({
                        "aria-expanded": "true"
                    });
                     setTimeout(function(){ /* Adam - 10.27.17 - 02964281 */
                     	$("#cs-search-input-cont #sw-search-input").focus();
                	}, 400);
                }).addClass("open");



            }
        });

        $("li.sw-channel-item.cs-search-icon a").keydown(function(e) {
        	if(e.keyCode == csKeyCodes.enter || e.keyCode == csKeyCodes.space) {
            	e.preventDefault();
                if($("li.sw-channel-item.cs-search-icon").hasClass('open')) {
                    $("#cs-search-input-cont").removeClass('open').attr({
                        "aria-expanded": "false"
                    });

                    $("li.sw-channel-item.cs-search-icon").delay(100).animate({
                        "margin-left": "0"
                    }, "fast", function() {
                        $(this).attr({
                            'aria-expanded': 'false'
                        });

                    }).removeClass("open");
                } else {
                    //$("li.sw-channel-item.cs-search-icon").addClass("open");
                    $("li.sw-channel-item.cs-search-icon").animate({
                        "margin-left": "-115px"
                    }, "fast", function() {
                        $(this).attr({
                            'aria-expanded': 'true'
                        });
                        $("#cs-search-input-cont").addClass('open').attr({
                            "aria-expanded": "true"
                        });
                        setTimeout(function() {
                            $("#cs-search-input-cont #sw-search-input").focus();
                        }, 400);
                    }).addClass("open");

                }
            }
        });

        $(".search-close-btn").click(function() {
            $("#cs-search-input-cont").removeClass('open').attr({
                    "aria-expanded": "false"
                });

            $("li.sw-channel-item.cs-search-icon").delay(100).animate({
                "margin-left": "0"
            }, "fast", function() {
                $(this).attr({
                    'aria-expanded': 'false'
                });

            }).removeClass("open");
        });

        $(".search-close-btn").keydown(function(e) {
        	if(e.keyCode == csKeyCodes.enter || e.keyCode == csKeyCodes.space) {
                $("#cs-search-input-cont").removeClass('open').attr({
                    "aria-expanded": "false"
                });

                $("li.sw-channel-item.cs-search-icon").delay(100).animate({
                    "margin-left": "0"
                }, "fast", function() {
                    $(this).attr({
                        'aria-expanded': 'false'
                    });

                }).removeClass("open");

                $(".cs-search-icon a").focus();
            }
        });

        if($("li.sw-channel-item.first > a span").text() == "Home") {
        	$("li.sw-channel-item.first").addClass("cs-home-icon channel-icon");
        	$("li.sw-channel-item.cs-home-icon > a span").addClass("plano-icon-font").html("");
            $("Home").appendTo("li.sw-channel-item.cs-home-icon > a");
            $("li.sw-channel-item.cs-home-icon").prependTo(".sw-channel-list");
            $("#navc-HP").show();
        }

        // TRANSLATE AND USER OPTIONS DROPDOWNS
        myStartDropdownActions({
            "dropdownParent": ".cs-mystart-dropdown.user-options",
            "dropdownSelector": ".cs-selector",
            "dropdown": ".cs-dropdown",
            "dropdownList": ".cs-dropdown-list"
        });

        $(".channel-icon > a").each(function() {
        	var thisIcon = this;
            $(thisIcon).hover(function(e) {
            	$(".icon-text").stop(true,true);
            	var thisHover = this;
                var textWidth = $(".icon-text",thisHover).width();
                textWidth = (textWidth / 2) + 10;
                $(".icon-text",thisHover).css({
                	"margin-left": "-" + textWidth + "px"
                }).fadeIn();
            }, function() {
            	$(".icon-text").stop(true,true);
            	var thisHover = this;
            	$(".icon-text").fadeOut();
            });

            //FOCUS
            $(thisIcon).focus(function(e) {
            	$(".icon-text").stop(true,true);
            	var thisHover = this;
                var textWidth = $(".icon-text",thisHover).width();
                textWidth = (textWidth / 2) + 10;
                $(".icon-text",thisHover).css({
                	"margin-left": "-" + textWidth + "px"
                }).fadeIn();
            });
            $(thisIcon).focusout(function() {
            	$(".icon-text").stop(true,true);
            	var thisHover = this;
            	$(".icon-text",thisHover).fadeOut();
            });
        });



        //DOC add aria to channels and dropdown items
		$(".sw-channel-item").attr("aria-haspopup","true");
		$(".sw-channel-dropdown li").attr("aria-haspopup","false");

		//UNBIND SYSTEM HOVER EVENT AND ADD CUSTOM HOVER EVENT
		$(".sw-channel-item").unbind('hover');
		$(".sw-channel-item").hover(function(e){
        	e.stopPropagation();
			var subList = $(this).children('ul');
			if ($.trim(subList.html()) !== "") {
				subList.slideDown(300, "swing");
			}
			$(this).addClass("hover");
		}, function(){
			$(".sw-channel-dropdown").slideUp(300, "swing");
			$(this).removeClass("hover");
		});

		//ADD TRANSITION CLASS TO ALL CHANNEL ITEMS
		$("ul.sw-channel-list li.sw-channel-item").addClass("transition");


    }

    function myStartDropdownActions(params) {
    	var keyCodes = { "tab": 9, "enter": 13, "esc": 27, "space": 32, "end": 35, "home": 36, "left": 37, "up": 38, "right": 39, "down": 40 };
        var dropdownParent = params.dropdownParent;
        var dropdownSelector = params.dropdownSelector;
        var dropdown = params.dropdown;
        var dropdownList = params.dropdownList;

        $(dropdownParent + " " + dropdownList + " a").attr("tabindex", "-1");

        // MYSTART DROPDOWN SELECTOR CLICK EVENT
        $(dropdownSelector).click(function(e) {
            e.preventDefault();

            if($(this).parent().hasClass("open")){
                $("+ " + dropdownList + " a").attr("tabindex", "-1");
                $(this).attr("aria-expanded", "false").parent().removeClass("open").find(dropdown).attr("aria-hidden", "true").slideUp(300, "swing"); 
            } else {
                $(this).attr("aria-expanded", "true").parent().addClass("open").find(dropdown).attr("aria-hidden","false").slideDown(300, "swing");
            }
        });

        // MYSTART DROPDOWN SELECTOR KEYDOWN EVENTS
        $(document).on("keydown", dropdownSelector, function(e) {
            // CAPTURE KEY CODE
            switch(e.keyCode) {
                // CONSUME LEFT AND UP ARROWS
                case keyCodes.enter:
                case keyCodes.space:
                    e.preventDefault();

                    // IF THE DROPDOWN IS OPEN, CLOSE IT
                    if($(dropdownParent).hasClass("open")){
                        $("+ " + dropdown + " " + dropdownList + " a").attr("tabindex", "-1");
                        $(this).attr("aria-expanded", "false").parent().removeClass("open").find(dropdown).attr("aria-hidden", "true").slideUp(300, "swing"); 
                    } else {
                        $(this).attr("aria-expanded", "true").parent().addClass("open").find(dropdown).attr("aria-hidden", "false").slideDown(300, "swing", function(){
                            $(dropdownList + " li:first-child a", this).attr("tabindex", "0").focus();
                        });
                    }
                break;

                // CONSUME TAB KEY
                case keyCodes.tab:
                    $("+ " + dropdown + " " + dropdownList + " a").attr("tabindex", "-1");
                    $(this).attr("aria-expanded", "false").parent().removeClass("open").find(dropdown).attr("aria-hidden", "true").slideUp(300, "swing");
                break;

                // CONSUME LEFT AND UP ARROWS
                case keyCodes.down:
                case keyCodes.right:
                    e.preventDefault();

                    $("+ " + dropdown + " " + dropdownList + " a").attr("tabindex", "-1");
                    $("+ " + dropdown + " " + dropdownList + " li:first-child > a", this).attr("tabindex", "0").focus();
                break;
            }
        });

        // MYSTART DROPDOWN LINK KEYDOWN EVENTS
        $(document).on("keydown", dropdownList + " li a", function(e) {
            // CAPTURE KEY CODE
            switch(e.keyCode) {
                // CONSUME LEFT AND UP ARROWS
                case keyCodes.left:
                case keyCodes.up:
                    e.preventDefault();

                    // IS FIRST ITEM
                    if($(this).parent().is(":first-child")) {
                        // FOCUS DROPDOWN BUTTON
                        $(this).closest(dropdownList).find("a").attr("tabindex", "-1");
                        $(this).closest(dropdownParent).find(dropdownSelector).focus();
                    } else {
                        // FOCUS PREVIOUS ITEM
                        $(this).closest(dropdownList).find("a").attr("tabindex", "-1");
                        $(this).parent().prev("li").find("> a").attr("tabindex", "0").focus();
                    }
                break;

                // CONSUME RIGHT AND DOWN ARROWS
                case keyCodes.right:
                case keyCodes.down:
                    e.preventDefault();

                    // IS LAST ITEM
                    if($(this).parent().is(":last-child")) {
                        // FOCUS FIRST ITEM
                        $(this).closest(dropdownList).find("a").attr("tabindex", "-1");
                        $(this).closest(dropdownList).find("li:first-child > a").attr("tabindex", "0").focus();
                    } else {
                        // FOCUS NEXT ITEM
                        $(this).closest(dropdownList).find("a").attr("tabindex", "-1");
                        $(this).parent().next("li").find("> a").attr("tabindex", "0").focus();
                    }
                break;

                // CONSUME TAB KEY
                case keyCodes.tab:


                    if(e.shiftKey) {
                        e.preventDefault();

                        // FOCUS DROPDOWN BUTTON
                        $(this).closest(dropdownList).find("a").attr("tabindex", "-1");
                        $(this).closest(dropdownParent).find(dropdownSelector).focus();
                    }
                break;

                // CONSUME HOME KEY
                case keyCodes.home:
                    e.preventDefault();

                    // FOCUS FIRST ITEM
                    $(this).closest(dropdownList).find("a").attr("tabindex", "-1");
                    $(this).closest(dropdownList).find("li:first-child > a").attr("tabindex", "0").focus();
                break;

                // CONSUME END KEY
                case keyCodes.end:
                    e.preventDefault();

                    // FOCUS LAST ITEM
                    $(this).closest(dropdownList).find("a").attr("tabindex", "-1");
                    $(this).closest(dropdownList).find("li:last-child > a").attr("tabindex", "0").focus();
                break;
            }
        });

        $(dropdownParent).mouseleave(function() {
            $(dropdownList + " a", this).attr("tabindex", "-1");
            $(dropdownSelector, this).attr("aria-expanded", "false").parent().removeClass("open").find(dropdown).attr("aria-hidden", "true").slideUp(300, "swing"); 
        }).focusout(function() {
            var thisDropdown = this;

            setTimeout(function () {
                if(!$(thisDropdown).find(":focus").length) {
                    $(dropdownSelector, thisDropdown).attr("aria-expanded", "false").parent().removeClass("open").find(dropdown).attr("aria-hidden", "true").slideUp(300, "swing");
                }
            }, 500);
        });
    }

    function sameHeights() {
    	if($(".hp").length) {

            $("body").csRsSameHeights({
                resizeElements : [
                    {
                        "resizeMethod"		: "apps",	// OPTIONS - columns, apps
                        "parent"			: "#hp-content-inner-top",	// PARENT THAT COLUMNS AND/OR APPS ARE IN THAT ARE GETTING RESIZED
                        "columns"			: ".outer-column",	// COLUMNS AND/OR APPS WITHIN THESE COLUMNS THAT SAME HEIGHTS IS BEING APPLIED TO -- NEEDS TO BE A CLASS
                        "gutter"			: 30,			// PIXEL SPACING BETWEEN APPS -- WILL BE ADDED AS BOTTOM MARGIN
                        "applySameHeights"	: ["960"]	// OPTIONS - ["960", "768", "640", "480", "320"]
                    }
                ]
            });
            $("body").csRsSameHeights({
                resizeElements : [
                    {
                        "resizeMethod"		: "apps",	// OPTIONS - columns, apps
                        "parent"			: "#hp-content-left-columns",	// PARENT THAT COLUMNS AND/OR APPS ARE IN THAT ARE GETTING RESIZED
                        "columns"			: ".column",	// COLUMNS AND/OR APPS WITHIN THESE COLUMNS THAT SAME HEIGHTS IS BEING APPLIED TO -- NEEDS TO BE A CLASS
                        "gutter"			: 30,			// PIXEL SPACING BETWEEN APPS -- WILL BE ADDED AS BOTTOM MARGIN
                        "applySameHeights"	: ["960"]	// OPTIONS - ["960", "768", "640", "480", "320"]
                    }
                ]
            });
        } /*else { COMMENTED OUT BY LYNDSAY - CASE 00527992 - 5.1.17
        	$("body").csRsSameHeights({ 
                resizeElements : [
                    {
                        "resizeMethod"		: "columns",	// OPTIONS - columns, apps
                        "parent"			: "#sp-content",	// PARENT THAT COLUMNS AND/OR APPS ARE IN THAT ARE GETTING RESIZED
                        "columns"			: ".column",	// COLUMNS AND/OR APPS WITHIN THESE COLUMNS THAT SAME HEIGHTS IS BEING APPLIED TO -- NEEDS TO BE A CLASS
                        "gutter"			: 0,			// PIXEL SPACING BETWEEN APPS -- WILL BE ADDED AS BOTTOM MARGIN
                        "applySameHeights"	: ["960"]	// OPTIONS - ["960", "768", "640", "480", "320"]
                    }
                ]
            });
        }*/
    }

    function schoolMegaMenu() {
        var schools = '', sortedSchools = {};

        // SORT THE SCHOOL LIST TO TRY TO GET THE EXPECTED TYPES IN THE ORDER WE WANT
        var expectedNameOrder = ["childhood", "elementary", "middle", "high", "senior", "academy"];
        schoolList.sort(function(a, b) {
            if(expectedNameOrder.indexOf(a.schoolType.toLowerCase()) < expectedNameOrder.indexOf(b.schoolType.toLowerCase())) {
                return -1;
            }
            if(expectedNameOrder.indexOf(a.schoolType.toLowerCase()) > expectedNameOrder.indexOf(b.schoolType.toLowerCase())) {
                return 1;
            }
            return 0;
        });

        // SORT THE SCHOOLS INTO TYPE OBJECTS
        for(var type in schoolList) {
            if(schoolList[type].schoolVisible.toLowerCase() == 'true') {
                var thisType = schoolList[type].schoolType.toLowerCase();

                // CHECK FOR MULTIPLE COMMA SEPARATED VALUES
                if(schoolList[type].schoolType.indexOf(",") > -1) {
                    var subTypes = schoolList[type].schoolType.split(",");

                    // LOOP THROUGH THE SUB TYPES
                    for(var subType in subTypes) {
                        var thisSubType = $.trim(subTypes[subType]).toLowerCase();

                        // ADD THIS TYPE OBJECT TO THE SORTED SCHOOLS OBJECT IF IT'S NOT THERE YET
                        if(!sortedSchools.hasOwnProperty(thisSubType)) {
                            // ADD THIS TYPE VALUE TO THE SORTED SCHOOLS OBJECT AND ADD THIS SCHOOL RECORD
                            sortedSchools[thisSubType] = [schoolList[type]];
                        } else {
                            // PUSH THIS SCHOOL RECORD TO THE EXISTING TYPE OBJECT
                            sortedSchools[thisSubType].push(schoolList[type]);
                        }
                    }
                }

                // SINGLE TYPE VALUE
                else {
                    // ADD THIS TYPE OBJECT TO THE SORTED SCHOOLS OBJECT IF IT'S NOT THERE YET
                    if(!sortedSchools.hasOwnProperty(thisType)) {
                        // ADD THIS TYPE VALUE TO THE SORTED SCHOOLS OBJECT AND ADD THIS SCHOOL RECORD
                        sortedSchools[thisType] = [schoolList[type]];
                    } else {
                        // PUSH THIS SCHOOL RECORD TO THE EXISTING TYPE OBJECT
                        sortedSchools[thisType].push(schoolList[type]);
                    }
                }
            }
        }

        // BUILD THE SCHOOL MENU STRUCTURE
        var schoolMenuTabs = '';
        var schoolMenuPanels = '

';
        $.each(sortedSchools, function(type, schools) {
            var typeName = type.replace(/\s/g, "-").replace(/&/g, "and");

            // SORT SCHOOLS ARRAY ALPHABETICALLY IN CASE SCHOOLS GOT SHIFTED A BIT FROM MULTIPLE TYPE VALUES
            schools.sort(function(a, b) {
                if(a.schoolName > b.schoolName) {
                    return 1;
                }
                if(a.schoolName < b.schoolName) {
                    return -1;
                }
                return 0;
            });
            // BUILD TAB
            schoolMenuTabs += '
' + typeName + '';

            // BUILD PANEL
            schoolMenuPanels += '
';
            for(var school in schools) {
                schoolMenuPanels += '
' + schools[school].schoolName + '';
            }
            schoolMenuPanels += '';
        });
        schoolMenuTabs += '';
        schoolMenuPanels += '';

        // ADD THE COMPLETED MENU TO THE DOM
        $("#gb-schoollist-menu").html(schoolMenuTabs + schoolMenuPanels);
        $(".gb-school-list-tab.other-learning-centers").appendTo("#gb-school-list-tabs");
        $(".gb-school-list-panel.other-learning-centers").appendTo("#gb-school-list-panels-inner");

        $(".gb-school-list-tab.childhood span:eq(0)").text("early childhood");
        $(".gb-school-list-tab.senior span:eq(0)").text("senior high");
        $(".gb-school-list-tab.academy span:eq(0)").text("academy programs");
        $(".gb-school-list-tab.other-learning-centers span:eq(0)").text("other learning centers");
        $("Grades Pre-K, PPCD").appendTo(".gb-school-list-tab.childhood");
        $("Grades K-5").appendTo(".gb-school-list-tab.elementary");
        $("Grades 6-8").appendTo(".gb-school-list-tab.middle");
        $("Grades 9-12").appendTo(".gb-school-list-tab.high");
        $("Grades 11-12").appendTo(".gb-school-list-tab.senior");
        $("Grades K-12").appendTo(".gb-school-list-tab.academy");

        //AJAX SITESHORTCUTS FROM DISTRICT HOMEPAGE INTO SCHOOL MENU
        /*if ($("#sp-content").length) {
            var homeURL = location.protocol + "//" + window.location.hostname;
            $.ajax({
                type: "GET",
                url: homeURL,
                cache: false,
