        csRsMenu(sortedSchools);
    }

    function schoolMenuControls() {
        // SCHOOLS BUTTON CLICK
        $(document).on("click keydown", ".global-icon.schools", function(e) {
        	if(e.type == "click" || e.type == "keydown" && e.keyCode == csKeyCodes.space || e.type == "keydown" && e.keyCode == csKeyCodes.enter) {
                e.preventDefault();
                handleSchoolMenuButtonAction();
            }
        });

        // SCHOOL TAB FOCUS
        $(".gb-school-list-tab").focus(function() {
        	// ADJUST TAB ATTRIBUTES
            $(".gb-school-list-tab").attr({"tabindex": "-1", "aria-selected": "false"}).removeClass("active");
            $(this).attr({"tabindex": "0", "aria-selected": "true"}).addClass("active");

            // ADJUST PANEL ATTRIBUTES
            $(".gb-school-list-panel").attr({"aria-hidden": "true"}).removeClass("active");
            $("#" + $(this).attr("aria-controls")).attr({"aria-hidden": "false"}).removeClass("active");
        });

        // IE TAB CLICK
        $(".ie .gb-school-list-tab span").click(function() {
        	$(this).parent().focus();
        });	

        // SCHOOL TAB KEYDOWN
        $(".gb-school-list-tab").keydown(function(e) {
        	handleTabListKey($(this), ".gb-school-list-tab", e);
        });

        // CLOSE MENU WITH ESC
        $(".gb-school-list-tab, .gb-school-list-panel").keydown(function(e) {
        	if(e.keyCode == csKeyCodes.esc) {
            	e.preventDefault();

                /*$("#gb-schoollist-menu-outer").slideUp(function() {
                    // RESET ATTRIBUTES
                    $(".gb-school-list-tab").attr({"tabindex": "-1", "aria-selected": "false"}).removeClass("active");
                    $(".gb-school-list-panel").attr({"aria-hidden": "true"}).removeClass("active");
                    $("global-icon.schools").focus();
                }).attr("aria-hidden", "true");*/
                if($(".global-icon.schools").attr("aria-expanded") == "false") {
            // MAKE SURE ALL ATTRIBUTES ARE RESET
            $(".gb-school-list-tab").attr({"tabindex": "-1", "aria-selected": "false"}).removeClass("active");
            $(".gb-school-list-panel").attr({"aria-hidden": "true"}).removeClass("active");

            // SET ELEMENTARY TAB AND PANEL AS ACTIVE
            $(".gb-school-list-tab:first").attr({"tabindex": "0", "aria-selected": "true"}).addClass("active");
            $(".gb-school-list-panel:first").attr({"aria-hidden": "false"}).addClass("active");

            // OPEN THE MENU
            $(window).scroll(function() {
            	var scrolled = $(window).scrollTop();
                if($(".hp").length) {
                  if(scrolled >= 45) {
                      $("#gb-schoollist-menu-outer").addClass("sticky-header");
                  } else {
                      $("#gb-schoollist-menu-outer").removeClass("sticky-header");
                  }		
                } else {
                	if(scrolled >= 191) {
                      $("#gb-schoollist-menu-outer").addClass("sp-sticky-header");
                  } else {
                      $("#gb-schoollist-menu-outer").removeClass("sp-sticky-header");
                  }
                }
            });

            $("#gb-schoollist-menu-outer").stop(true, true).slideDown(function() {
                $(".gb-school-list-tab:first").focus();
                $(".global-icon.schools").attr({
                    "aria-expanded": "true",
                    "aria-label": "Close Schools Menu"
                });
            }).attr("aria-hidden", "false");
			var channelBarHeight = $("#gb-channel-list-outer").height();
            channelBarHeight = 245 + channelBarHeight;
            $("#gb-schoollist-menu-outer").css("top",channelBarHeight);


            $("#gb-global-icons .global-icon span.text.schools-text, .gb-sticky-global-icons .global-icon span.text.schools-text").fadeOut(function() {
                $("#gb-global-icons .global-icon span.text.close-text").fadeIn().css("display","block");
                $(".gb-sticky-global-icons .global-icon span.text.close-text").fadeIn().css({"display":"inline-block", "margin-top":"10px"});
            });
            $("#gb-global-icons .global-icon .cs-schools-icon.schools-icon, .gb-sticky-global-icons .global-icon .cs-schools-icon.schools-icon").fadeOut(function() {
                $("#gb-global-icons .global-icon span.cs-schools-icon.close-icon").fadeIn().css("display","block");
                $(".gb-sticky-global-icons .global-icon .cs-schools-icon.close-icon").fadeIn().css("display","inline-block");
            });

            //REMOVE BORDER FROM LINK ABOVE HOVERED LINK
            $("#gb-schoollist-menu li").hover(function() {
            	var thisItem = this;
                var itemAbove = $(this).prev().prev().prev().prev();
                $(itemAbove).addClass("no-border");
            }, function() {
            	var thisItem = this;
                var itemAbove = $(this).prev().prev().prev().prev();
                $(itemAbove).removeClass("no-border");
            });

        }

        // MENU IS OPEN
        else {
            // CLOSE THE MENU
            $("#gb-schoollist-menu-outer").stop(true, true).slideUp(function() {
                // RESET ATTRIBUTES
                $(".gb-school-list-tab").attr({"tabindex": "-1", "aria-selected": "false"}).removeClass("active");
                $(".gb-school-list-panel").attr({"aria-hidden": "true"}).removeClass("active");
                $(".global-icon.schools").attr({
                    "aria-expanded": "false",
                    "aria-label": "Open Schools Menu"
                });

            }).attr("aria-hidden", "true");


            $("#gb-global-icons .global-icon span.text.close-text, .gb-sticky-global-icons .global-icon span.text.close-text").fadeOut(function() {
                	$("#gb-global-icons .global-icon span.text.schools-text").fadeIn().css("display","block");
                    $(".gb-sticky-global-icons .global-icon span.text.schools-text").fadeIn().css({"display":"inline-block", "margin-top":"10px"});
                });
                $("#gb-global-icons .global-icon span.cs-schools-icon.close-icon, .gb-sticky-global-icons .global-icon span.cs-schools-icon.close-icon").fadeOut(function() {
                	$("#gb-global-icons .global-icon .cs-schools-icon.schools-icon").fadeIn().css("display","block");
                    $(".gb-sticky-global-icons .global-icon .cs-schools-icon.schools-icon").fadeIn().css("display","inline-block");
                });
        }

                $(".global-icon.schools").attr("aria-expanded", "false").focus();
            }
        });

        // SCHOOL MENU LINK KEYDOWN
        $(".gb-school-list-panel a").keydown(function(e) {
            switch(e.keyCode){
                // CONSUME LEFT AND UP ARROWS
                case csKeyCodes.left:
                	e.preventDefault();

                    // FOCUS THE PREVIOUS ITEM
                    if ($(this).parent().is(":first-child")) { 
                        $(this).parent().parent().find("li:last-child a").focus();
                    } else { 
                        $(this).parent().prev("li").find("a").focus();
                    } 
                break;
                case csKeyCodes.up:
                    e.preventDefault();

                    // FOCUS THE ABOVE ITEM
                    if ($(this).parent().parent().find("li:nth-child(" + (($(this).parent().index() + 1) - 4) + ")").length) { 
                        $(this).parent().parent().find("li:nth-child(" + (($(this).parent().index() + 1) - 4) + ") a").focus();
                    } else { 
                        return false;
                    } 
                break;

                // CONSUME RIGHT AND DOWN ARROWS
                case csKeyCodes.right:
                	// FOCUS THE NEXT ITEM
                    if ($(this).parent().is(":last-child")) { 
                        //$(this).parent().parent().find("li:first-child a").focus();
                        $(".schoollist-extra-links li:first-child a").focus();
                    } else { 
                        $(this).parent().next("li").find("a").focus();
                    } 
                break;
                case csKeyCodes.down:
                    e.preventDefault();

                    // FOCUS THE BELOW ITEM
                    if ($(this).parent().parent().find("li:nth-child(" + (($(this).parent().index() + 1) + 4) + ")").length) { 
                        $(this).parent().parent().find("li:nth-child(" + (($(this).parent().index() + 1) + 4) + ") a").focus();
                    } else { 
                        return false;
                    } 
                break;

                // CONSUME HOME KEY
                case csKeyCodes.home:
                    e.preventDefault();

                    // FOCUS THE FIRST ITEM
                    $(this).parent().parent().find("li:first-child a").focus();
                break;

                // CONSUME END KEY
                case csKeyCodes.end:
                    e.preventDefault();

                    // FOCUS THE LAST ITEM
                    $(this).parent().parent().find("li:last-child a").focus();
                break;

                case csKeyCodes.tab:
                	//e.preventDefault();
                    if ($(this).parent().is(":last-child")) { 
                    	$(".schoollist-extra-links li:first-child a").focus();
                    }
                break;
            }
        });
    }

    function handleSchoolMenuButtonAction() {
    	// MENU IS NOT OPEN
        if($(".global-icon.schools").attr("aria-expanded") == "false") {
            // MAKE SURE ALL ATTRIBUTES ARE RESET
            $(".gb-school-list-tab").attr({"tabindex": "-1", "aria-selected": "false"}).removeClass("active");
            $(".gb-school-list-panel").attr({"aria-hidden": "true"}).removeClass("active");

            // SET ELEMENTARY TAB AND PANEL AS ACTIVE
            $(".gb-school-list-tab:first").attr({"tabindex": "0", "aria-selected": "true"}).addClass("active");
            $(".gb-school-list-panel:first").attr({"aria-hidden": "false"}).addClass("active");

            // OPEN THE MENU
            $(window).scroll(function() {
            	var scrolled = $(window).scrollTop();
                if($(".hp").length) {
                  if(scrolled >= 45) {
                      $("#gb-schoollist-menu-outer").addClass("sticky-header");
                  } else {
                      $("#gb-schoollist-menu-outer").removeClass("sticky-header");
                  }	
                } else {
                	if(scrolled >= 191) {
                      $("#gb-schoollist-menu-outer").addClass("sp-sticky-header");
                  } else {
                      $("#gb-schoollist-menu-outer").removeClass("sp-sticky-header");
                  }
                }
            });

            $("#gb-schoollist-menu-outer").stop(true, true).slideDown(function() {
                $(".gb-school-list-tab:first").focus();
                $(".global-icon.schools").attr({
                    "aria-expanded": "true",
                    "aria-label": "Close Schools Menu"
                });
            }).attr("aria-hidden", "false");



            $("#gb-global-icons .global-icon span.text.schools-text, .gb-sticky-global-icons .global-icon span.text.schools-text").fadeOut(function() {
                $("#gb-global-icons .global-icon span.text.close-text").fadeIn().css("display","block");
                $(".gb-sticky-global-icons .global-icon span.text.close-text").fadeIn().css({"display":"inline-block", "margin-top":"10px"});
            });
            $("#gb-global-icons .global-icon .cs-schools-icon.schools-icon, .gb-sticky-global-icons .global-icon .cs-schools-icon.schools-icon").fadeOut(function() {
                $("#gb-global-icons .global-icon span.cs-schools-icon.close-icon").fadeIn().css("display","block");
                $(".gb-sticky-global-icons .global-icon .cs-schools-icon.close-icon").fadeIn().css("display","inline-block");
            });

            //REMOVE BORDER FROM LINK ABOVE HOVERED LINK
            $("#gb-schoollist-menu li").hover(function() {
            	var thisItem = this;
                var itemAbove = $(this).prev().prev().prev().prev();
                $(itemAbove).addClass("no-border");
            }, function() {
            	var thisItem = this;
                var itemAbove = $(this).prev().prev().prev().prev();
                $(itemAbove).removeClass("no-border");
            });

        }

        // MENU IS OPEN
        else {
            // CLOSE THE MENU
            $("#gb-schoollist-menu-outer").stop(true, true).slideUp(function() {
                // RESET ATTRIBUTES
                $(".gb-school-list-tab").attr({"tabindex": "-1", "aria-selected": "false"}).removeClass("active");
                $(".gb-school-list-panel").attr({"aria-hidden": "true"}).removeClass("active");
                $(".global-icon.schools").attr({
                    "aria-expanded": "false",
                    "aria-label": "Open Schools Menu"
                });

            }).attr("aria-hidden", "true");


            $("#gb-global-icons .global-icon span.text.close-text, .gb-sticky-global-icons .global-icon span.text.close-text").fadeOut(function() {
                	$("#gb-global-icons .global-icon span.text.schools-text").fadeIn().css("display","block");
                    $(".gb-sticky-global-icons .global-icon span.text.schools-text").fadeIn().css({"display":"inline-block", "margin-top":"10px"});
                });
                $("#gb-global-icons .global-icon span.cs-schools-icon.close-icon, .gb-sticky-global-icons .global-icon span.cs-schools-icon.close-icon").fadeOut(function() {
                	$("#gb-global-icons .global-icon .cs-schools-icon.schools-icon").fadeIn().css("display","block");
                    $(".gb-sticky-global-icons .global-icon .cs-schools-icon.schools-icon").fadeIn().css("display","inline-block");
                });
        }
    }

    function handleTabListKey(tab, tabClass, event) {
    	var lastIndex = $(tabClass).length - 1;

        switch(event.keyCode){
        	// CONSUME LEFT AND UP ARROWS
            case csKeyCodes.left:
            case csKeyCodes.up:
            	event.preventDefault();

                // FOCUS THE PREVIOUS TAB
                if (tab.index() == 0) { 
                    $(tabClass + ":last-child").focus();
                } else { 
                    $(tabClass).eq(tab.index() - 1).focus();
                } 
            break;

            // CONSUME RIGHT AND DOWN ARROWS
            case csKeyCodes.right:
            case csKeyCodes.down:
            	event.preventDefault();

                // FOCUS THE NEXT TAB
                if (tab.index() == lastIndex) { 
                    $(tabClass + ":first-child").focus();
                } else { 
                    $(tabClass).eq(tab.index() + 1).focus();
                }
            break;

            // CONSUME HOME KEY
            case csKeyCodes.home:
            	event.preventDefault();

                // FOCUS THE FIRST TAB
                $(tabClass + ":first-child").focus();
            break;

            // CONSUME END KEY
            case csKeyCodes.end:
            	event.preventDefault();

                // FOCUS THE LAST TAB
                $(tabClass + ":last-child").focus();
            break;
        }
    }

	function jsMediaQueries() {
		switch(csGetBreakPoint()) {
			case "desktop": 
				desktopHeadlines();
                $(".content-left.top .ui-widget.app.upcomingevents, .content-left.bottom .ui-widget.app.upcomingevents, .hp-content.bottom .ui-widget.app.upcomingevents").each(function() {
                    $(this).addClass("cs-desktop-events");
                });
                if($("#gb-channel-list-outer").height() > 55) {
                    $("#gb-sticky-icons").css("top","220px");
                } else {
                	$("#gb-sticky-icons").css("top","160px");
                }
			break;
			case "768":
            	desktopHeadlines();
				mobileSchoolsBtn();
                if($(window).width() < 1024 && !$("#sw-content-container10 #gb-mobile-icons").length) {
                    $("#gb-mobile-icons").appendTo("#sw-content-container10");
                }
                spAccordion();
			break;
			case "640":
            	desktopHeadlines();
				mobileSchoolsBtn();
                if($(window).width() < 1024 && !$("#sw-content-container10 #gb-mobile-icons").length) {
                    $("#gb-mobile-icons").appendTo("#sw-content-container10");
                }
                spAccordion();
			break;
			case "480":
            	desktopHeadlines();
            	mobileSchoolsBtn();
                if($(window).width() < 1024 && !$("#sw-content-container10 #gb-mobile-icons").length) {
                    $("#gb-mobile-icons").appendTo("#sw-content-container10");
                }
                spAccordion();
			break;
			case "320":
            	desktopHeadlines();
            	mobileSchoolsBtn();
                if($(window).width() < 1024 && !$("#sw-content-container10 #gb-mobile-icons").length) {
                    $("#gb-mobile-icons").appendTo("#sw-content-container10");
                }
                spAccordion();
			break;
		}
	}

    function spAccordion() {

    	if($(".sp").length) {//DOC accordion header click
          $(".sp.column.one div.ui-widget-header").off('click').click(function(){
              var clickedApp = $(this).parent();

              if($(clickedApp).hasClass('active')){
                  $(clickedApp).removeClass('active');
              }else{
                  $(".sp.column.one div.ui-widget.app.active").removeClass("active");
                  $(this).parent().addClass("active");
              }

              smoothScroll("#" + $(clickedApp).parent().attr("id"), 10);
          });
        }
	}

    function mobileSchoolsBtn() {
    	if($(window).width() < 1024) {
          $(".global-icon.schools").click(function(e) {
              if($(window).width() < 1024) { /* Added by Jeremy K to prevent mobile click events involving this button from processing on the desktop; this was the easiest work around.  - Case #0054535 */
                  e.preventDefault();
                  $("#rs-menu-btn").click();
              }
          });
        }else{

        }
    }

    function desktopHeadlines() {
    	if($(window).width() >= 1024) {
        	$(".content-left.top .ui-widget.app.headlines, .content-left.bottom .ui-widget.app.headlines, .hp-content.bottom .ui-widget.app.headlines").each(function() {
            	$(this).addClass("cs-desktop-headlines");
            });

            $(".content-left.top .ui-widget.app.headlines.cs-desktop-headlines .ui-article, .content-left.bottom .ui-widget.app.headlines.cs-desktop-headlines .ui-article, .hp-content.bottom .ui-widget.app.headlines.cs-desktop-headlines .ui-article").each(function() {
                var thisArticle = this;
                if(!$(".cs-article-inner-inner",thisArticle).length) {
                    $(thisArticle).wrapInner("
");
                    $(".ui-article-title",thisArticle).prependTo(thisArticle);
                }

                if(!$(".cs-article-inner",thisArticle).length) {
                    $(thisArticle).wrapInner("
");
                    $(".ui-article-thumb",thisArticle).prependTo(thisArticle);
                }

                if(!$(".headline-color-hover",thisArticle).length) {
                    $("
").prependTo(thisArticle);
                }

                /*if($(".cs-article-inner-inner .ui-article-description",thisArticle).text() == "") {
                    $(thisArticle).addClass("cs-no-desc");
                    $(".custom-teaser-read-more",thisArticle).remove();
                }*/
            });

            $(".headlines.cs-desktop-headlines li .ui-article").not($(".cs-no-desc")).hover(function() {
                $(".cs-article-inner-inner",this).slideDown();
            }, function() {
                $(".cs-article-inner-inner",this).slideUp();
            });


            $(".headlines.cs-desktop-headlines li .ui-article .ui-article-title a").focus(function() {
                var thisParent = $(this).parent().parent();
                $(".cs-article-inner-inner",thisParent).slideDown();
            });

            //REGION C HEADLINE HOVER GRADIENT
            var contentLeftTopColor1 = "rgba("+hexToRgb('#9c1f2e')+",0.6)";
            var contentLeftTopColor2 = "rgba("+hexToRgb('#9c1f2e')+",0.9)";
            $(".content-left.top .headlines.cs-desktop-headlines .headline-color-hover").css({
                "background": "url(data:image/svg+xml;base64,)",
                "background": "-moz-linear-gradient(top, " +  contentLeftTopColor1 + " 0%," + contentLeftTopColor2 + " 100%)", /* FF3.6-15 */
                "background": "-webkit-linear-gradient(top, " +  contentLeftTopColor1 + " 0%," + contentLeftTopColor2 + " 100%)", /* Chrome10-25,Safari5.1-6 */
                "background": "linear-gradient(to bottom, " +  contentLeftTopColor1 + " 0%," + contentLeftTopColor2 + " 100%)", /* W3C, IE10+, FF16+, Chrome26+, Opera12+, Safari7+ */
                "filter": "progid:DXImageTransform.Microsoft.gradient( startColorstr='#9c1f2e', endColorstr='#9c1f2e',GradientType=0 )" /* IE6-8 */
            });

            //REGION F HEADLINE HOVER GRADIENT
            var contentBottomTopColor1 = "rgba("+hexToRgb('#005BBB')+",0.6)";
            var contentBottomTopColor2 = "rgba("+hexToRgb('#005BBB')+",0.9)";
            $(".content-left.bottom .headlines.cs-desktop-headlines .headline-color-hover").css({
                "background": "url(data:image/svg+xml;base64,)",
                "background": "-moz-linear-gradient(top, " +  contentBottomTopColor1 + " 0%," + contentBottomTopColor2 + " 100%)", /* FF3.6-15 */
                "background": "-webkit-linear-gradient(top, " +  contentBottomTopColor1 + " 0%," + contentBottomTopColor2 + " 100%)", /* Chrome10-25,Safari5.1-6 */
                "background": "linear-gradient(to bottom, " +  contentBottomTopColor1 + " 0%," + contentBottomTopColor2 + " 100%)", /* W3C, IE10+, FF16+, Chrome26+, Opera12+, Safari7+ */
                "filter": "progid:DXImageTransform.Microsoft.gradient( startColorstr='#005BBB', endColorstr='#005BBB',GradientType=0 )" /* IE6-8 */
            });

            //REGION H HEADLINE HOVER GRADIENT
            var contentHTopColor1 = "rgba("+hexToRgb('#9c1f2e')+",0.6)";
            var contentHTopColor2 = "rgba("+hexToRgb('#9c1f2e')+",0.9)";
            $(".hp-content.bottom .headlines.cs-desktop-headlines .headline-color-hover").css({
                "background": "url(data:image/svg+xml;base64,)",
                "background": "-moz-linear-gradient(top, " +  contentHTopColor1 + " 0%," + contentHTopColor2 + " 100%)", /* FF3.6-15 */
                "background": "-webkit-linear-gradient(top, " +  contentHTopColor1 + " 0%," + contentHTopColor2 + " 100%)", /* Chrome10-25,Safari5.1-6 */
                "background": "linear-gradient(to bottom, " +  contentHTopColor1 + " 0%," + contentHTopColor2 + " 100%)", /* W3C, IE10+, FF16+, Chrome26+, Opera12+, Safari7+ */
                "filter": "progid:DXImageTransform.Microsoft.gradient( startColorstr='#9c1f2e', endColorstr='#9c1f2e',GradientType=0 )" /* IE6-8 */
            });

            $(".headlines.cs-desktop-headlines li").hover(function() {
                $(".headline-color-hover",this).fadeIn();
            }, function() {
                $(".headline-color-hover",this).fadeOut();
            });
        } else {
           	$(".headlines.cs-desktop-headlines").removeClass("cs-desktop-headlines");
            $(".headlines .ui-article").addClass("ui-clear").off();
        }

        var dynStyleSheet = document.createElement('style');

		if(dynStyleSheet) {
			dynStyleSheet.setAttribute('type', 'text/css');
			var head = document.getElementsByTagName('head')[0];

			if(head) {
				head.insertBefore(dynStyleSheet, head.childNodes[0]);
			}

			var dynStyles = ".content-left.top .headlines.cs-desktop-headlines .cs-article-inner:before {" +
                                "background: url(data:image/svg+xml;base64,);" +
                                "background: -moz-linear-gradient(top, " +  contentLeftTopColor1 + " 0%," + contentLeftTopColor2 + " 100%);" +
                                "background: -webkit-linear-gradient(top, " +  contentLeftTopColor1 + " 0%," + contentLeftTopColor2 + " 100%);" +
                                "background: linear-gradient(to bottom, " +  contentLeftTopColor1 + " 0%," + contentLeftTopColor2 + " 100%);" +
                                "filter: progid:DXImageTransform.Microsoft.gradient( startColorstr='#9c1f2e', endColorstr='#9c1f2e',GradientType=0 );" +
                            "}" +
                            ".content-left.bottom .headlines.cs-desktop-headlines .cs-article-inner:before {" +
                            	"background: url(data:image/svg+xml;base64,);" +
                                "background: -moz-linear-gradient(top, " +  contentBottomTopColor1 + " 0%," + contentBottomTopColor2 + " 100%);" +
                                "background: -webkit-linear-gradient(top, " +  contentBottomTopColor1 + " 0%," + contentBottomTopColor2 + " 100%);" +
                                "background: linear-gradient(to bottom, " +  contentBottomTopColor1 + " 0%," + contentBottomTopColor2 + " 100%);" +
                                "filter: progid:DXImageTransform.Microsoft.gradient( startColorstr='#005BBB', endColorstr='#005BBB',GradientType=0 );" +
                            "}" +
                            ".hp-content.bottom .headlines.cs-desktop-headlines .cs-article-inner:before {" +
                            	"background: url(data:image/svg+xml;base64,);" +
                                "background: -moz-linear-gradient(top, " +  contentHTopColor1 + " 0%," + contentHTopColor2 + " 100%);" +
                                "background: -webkit-linear-gradient(top, " +  contentHTopColor1 + " 0%," + contentHTopColor2 + " 100%);" +
                                "background: linear-gradient(to bottom, " +  contentHTopColor1 + " 0%," + contentHTopColor2 + " 100%);" +
                                "filter: progid:DXImageTransform.Microsoft.gradient( startColorstr='#9c1f2e', endColorstr='#9c1f2e',GradientType=0 );" +
                            "}";

			var rules = document.createTextNode(dynStyles);

			if(dynStyleSheet.styleSheet){ // IE
				dynStyleSheet.styleSheet.cssText = dynStyles;
			} else {
				dynStyleSheet.appendChild(rules);
			}
		}
    }

