                }
        });

        $("#gb-global-icons .global-icon.help, #gb-sticky-header .global-icon.help, #gb-mobile-icons .global-icon.help").click(function(){
        	if(!$(this).hasClass("open")){
                $(this).addClass("open");
                $("#gb-sticky-header .global-icon.help").addClass("open").attr("aria-expanded","true");
                $("#gb-global-icons .global-icon.help").addClass("open").attr("aria-expanded","true");
                $("#gb-global-icon-overlay").fadeIn();
                $("#gb-global-icon-overlay .global-icon:first-child").focus();
            } else {
            	$(this).removeClass("open");
                $("#gb-sticky-header .global-icon.help").removeClass("open").attr("aria-expanded","false");
                $("#gb-global-icons .global-icon.help").removeClass("open").attr("aria-expanded","false");
                $("#gb-global-icon-overlay").fadeOut();
            }
        });

        $("#gb-global-icons .global-icon.help, #gb-sticky-header .global-icon.help").keydown(function(e) {
        	if(e.keyCode == csKeyCodes.enter || e.keyCode == csKeyCodes.space) {
            	e.preventDefault();
                if(!$(this).hasClass("open")){
                    $(this).addClass("open");
                    $("#gb-sticky-header .global-icon.help").addClass("open").attr("aria-expanded","true");
                    $("#gb-global-icons .global-icon.help").addClass("open").attr("aria-expanded","true");
                    $("#gb-global-icon-overlay").fadeIn();
                    $("#gb-global-icon-overlay .global-icon:first-child").focus();
                } else {
                    $(this).removeClass("open");
                    $("#gb-sticky-header .global-icon.help").removeClass("open").attr("aria-expanded","false");
                    $("#gb-global-icons .global-icon.help").removeClass("open").attr("aria-expanded","false");
                    $("#gb-global-icon-overlay").fadeOut();
                }
            }
        });


    }

    function csRsMenu(sortedSchools) {

    	var lightboxHeader = "", lightboxLinks = "";

        // MENU GLOBAL ICONS
        var lightboxIcons = [
            ["New Student Enrollment", "/domain/146"],
            ["Annual Registration", "/Page/17458"],
            ["Get Involved", "/Domain/265"],
            ["Resolving Student & Parent Concerns", "/page/20314"],
            ["FAQ", "/Domain/11101"],
            ["PASAR After School Care", "/pasar"],
            ["Bullying", "/Page/947"],
            ["Tipline", "/tipline"],
            ["Transportation", "/transportation"],
            ["Employee Benefits", "/benefits"]
        ];

        for(var i = 0; i < 10; i++) {
            lightboxLinks += "1, " + lightboxIcons[i][0] + ", " + lightboxIcons[i][1] + ", _self;";
        }

        if(i > 0) {
            lightboxHeader = "Get Help;";
        }

    	//ADD SCHOOLS FROM MEGA MENU
    	var headerCounter = 0;

        // BUILD THE SCHOOL MENU STRUCTURE
        var schoolHeaders = "";
        var schoolItems = "";
        $.each(sortedSchools, function(type, schools) {
            schoolHeaders += ((headerCounter > 0) ? ";" : "") + type;
            headerCounter++;

            for(var school in schools) {
            	schoolItems += (headerCounter + 2) + ',' + schools[school].schoolName + ',' + schools[school].schoolUrl + ',_self;';
            }
        });



    	// RESPONSIVE MENU
		$("body").csRsMenu({
			"breakPoint"			: 768, // SYSTEM BREAK POINTS - 768, 640, 480, 320
			"primaryColor"          : "#1A1A1A", // DEFAULT - #1A1A1A
		    "secondaryColor"        : "#0C0C0C", // DEFAULT - #0C0C0C
		    "accentColor"           : "#BABABA", // DEFAULT - #BABABA
			"menuButtonParent"		: "#gb-channel-list",
			"showMenuBtnText"		: "yes",
			"menuBtnText"			: "Menu",
			"showSchools"			: "no",
			"showTranslate"			: "yes",
			"translateId"			: "none",
			"showAccount"			: "yes",
			"extraMenuOptions"		: lightboxHeader + "Additional School Resources;" + schoolHeaders, // EXTRA MENU HEADING NAMES SEPARATED BY A ;
			"extraMenuItems"		: lightboxLinks + schoolItems, // EXTRA MENU BUTTONS - STRUCTURE IS: MENU-OPTION-NUMBER, ITEM-NAME, ITEM-URL, ITEM-TARGET;
			"siteID"				: "106"
		});



        $(".rs-menu-option.childhood h1").text("EARLY CHILDHOOD");
        $(".rs-menu-option.elementary h1").text("ELEMENTARY");
        $(".rs-menu-option.middle h1").text("MIDDLE");
        $(".rs-menu-option.high h1").text("HIGH");
        $(".rs-menu-option.senior h1").text("SENIOR HIGH");
        $(".rs-menu-option.academy h1").text("ACADEMY PROGRAMS");
        $(".rs-menu-option.other-learning-centers h1").text("OTHER LEARNING CENTERS");

        $(".rs-menu-option.other-learning-centers").appendTo("#rs-menu");

        $(".rs-menu-option.get-help").insertAfter("#rs-menu .rs-menu-channels");

        var calIcon = $(".global-icon.calendar").clone();
        calIcon.wrap("
");
        var getCalIcon = calIcon.parent().html();
       	var csIcon1 = $(".global-icon.icon3").clone();
        csIcon1.wrap("
");
        var getIcon1 = csIcon1.parent().html();
		var csIcon2 = $(".global-icon.icon4").clone();
        csIcon2.wrap("
");
        var getIcon2 = csIcon2.parent().html();
        var csIcon3 = $(".global-icon.icon5").clone();
        csIcon3.wrap("
");
        var getIcon3 = csIcon3.parent().html();

        var channelIcons = "

" + getCalIcon + "" +
        				   "

" + getIcon1 + "" +
                           "

" + getIcon2 + "" +
                           "

" + getIcon3 + "";
        $(channelIcons).appendTo(".rs-menu-channels .rs-menu-option-list");

        //added 3 lines below by Lyndsay - Case 00553577 - 6.5.17
        $(".custom-menu-icon-one:contains('undefined')").remove();
        $(".custom-menu-icon-two:contains('undefined')").remove();
        $(".custom-menu-icon-three:contains('undefined')").remove();
    }

	function mediaRotator(){
        if($('.hp').length) {
            var moduleID = $("#sw-content-container10.ui-hp div.ui-widget.app.cs-rs-multimedia-rotator").parent().attr("id").replace("module-content-", "");
            var dataArray = eval("appData" + moduleID);

          if('false' == 'true') {
              if(!$("#sw-content-container10.region.ui-hp .rs-gallery-images").length){
                  $("#sw-content-container10.ui-hp div.ui-widget.app.cs-rs-multimedia-rotator").csRsMultimediaRotator({
                      "imageWidth" 						: 1500,
                          "imageHeight" 				: 915,
                          "appHeader" 					: "no",
                          "titles" 						: "yes",
                          "captions" 					: "yes",
                          "galleryOverlay" 				: "no",
                          "linkedElement" 				: ["image"], // ["image", "title", "overlay", "all"]
                          "playPause" 					: "no",
                          "mainControls" 				: "yes",
                          "contMoreLink" 				: "no",
                          "contMoreLinkText" 			: "Read More",
                          "descMoreLink" 				: "yes",
                          "descMoreLinkText" 			: "Read More",
                          "descVideoLink" 				: "no",
                          "descVideoLinkText" 			: "click to play video",
                          "bullets" 					: "yes",
                          "bulletControls" 				: "no",
                          "thumbnails" 					: "no",
                          "thumbnailWidth" 				: 122,
                          "thumbnailHeight" 			: 53,
                          "thumbnailSlideDirection" 	: "horizontal", // horizontal or vertical
                          "hoverPause" 					: "yes",
                          "fullScreenRotator" 			: "yes", // image dimensions work well at 1500x915
                          "fullScreenBreakpoints"   	: ["960"], // OPTIONS ARE ["960", "768", "640", "480", "320"]
                          "fullScreenVideo" 			: "no",
                          "transitionType" 				: "fade", // fade or slide
                          "transitionSpeed" 			: 3,
                          "transitionDelay" 			: 8,
                          "autoRotate" 					: "yes",
                          "onImageLoad" 				: function() { },
                          "allImagesLoaded" 			: function() { },
                          "onThumbnailLoad" 			: function() { },
                          "allThumbnailsLoaded" 		: function() { },
                          "onTransitionStart" 			: function() {
                            var recordIndex = $("#hp-slideshow .rs-gallery-images-list .image.next").attr("index");
                            var currentLink = $.trim(dataArray[recordIndex][10]);

                            if(currentLink.indexOf("youtube.com") > -1) {
                                $("#hp-slideshow .rs-gallery-desc-more-link").addClass("video");
                                $("#hp-slideshow .rs-gallery-desc-more-link a span").text("Click to Play Video");
                            } else {
                                $("#hp-slideshow .rs-gallery-desc-more-link").removeClass("video");
                                $("#hp-slideshow .rs-gallery-desc-more-link a span").text("Read More");
                            }
                          },
                          "onTransitionEnd" 			: function() { },
                          "watchVidClick" 				: function() { },
                          "closeVidClick" 				: function() { },
                          "allLoaded" 					: function() {
                            var currentLink = $.trim(dataArray[0][10]);

                            if(currentLink.indexOf("youtube.com") > -1) {
                                $("#hp-slideshow .rs-gallery-desc-more-link").addClass("video");
                                $("#hp-slideshow .rs-gallery-desc-more-link a span").text("Click to Play Video");
                            } else {
                                $("#hp-slideshow .rs-gallery-desc-more-link").removeClass("video");
                                $("#hp-slideshow .rs-gallery-desc-more-link a span").text("Read More");
                            }

                            if(!$(".rs-gallery-desc-outer").length) {
                              $(".rs-gallery-desc").wrap("
").children().wrapAll('
');
                            }

                            $(".rs-gallery-bullets-outer").appendTo(".rs-gallery-container");

                            if($(window).width() < 1024) {
                                $(".rs-gallery-control.back, .rs-gallery-control.next").appendTo(".rs-gallery-images");
                            }
                          }
                  });
              }
          } else {
              $("#hp-slideshow-outer, #hp-slideshow, #hp-content-outer, #gb-mystart-outer").addClass("small-gallery");
              if(!$("#sw-content-container10.region.ui-hp .rs-gallery-images").length){
                $("#sw-content-container10.ui-hp div.ui-widget.app.cs-rs-multimedia-rotator").csRsMultimediaRotator({
                    "imageWidth" 					: 1123,
                        "imageHeight" 				: 618,
                        "appHeader" 				: "no",
                        "titles" 					: "yes",
                        "captions" 					: "yes",
                        "galleryOverlay" 			: "no",
                        "linkedElement" 			: ["image"], // ["image", "title", "overlay", "all"]
                        "playPause" 				: "no",
                        "mainControls" 				: "yes",
