                        "transitionDelay" 			: 8,
                        "autoRotate" 				: "yes",
                        "onImageLoad" 				: function() { },
                        "allImagesLoaded" 			: function() { },
                        "onThumbnailLoad" 			: function() { },
                        "allThumbnailsLoaded" 		: function() { },
                        "onTransitionStart" 		: function() {
                        	var currentLink = $.trim(dataArray[0][10]);

                            if(currentLink.indexOf("youtube.com") > -1) {
                                $("#hp-slideshow .rs-gallery-desc-more-link").addClass("video");
                                $("#hp-slideshow .rs-gallery-desc-more-link a span").text("Click to Play Video");
                            } else {
                                $("#hp-slideshow .rs-gallery-desc-more-link").removeClass("video");
                                $("#hp-slideshow .rs-gallery-desc-more-link a span").text("Read More");
                            }
                        },
                        "onTransitionEnd" 			: function() { },
                        "watchVidClick" 			: function() { },
                        "closeVidClick" 			: function() { },
                        "allLoaded" 				: function() {
                        	var currentLink = $.trim(dataArray[0][10]);

                            if(currentLink.indexOf("youtube.com") > -1) {
                                $("#hp-slideshow .rs-gallery-desc-more-link").addClass("video");
                                $("#hp-slideshow .rs-gallery-desc-more-link a span").text("Click to Play Video");
                            } else {
                                $("#hp-slideshow .rs-gallery-desc-more-link").removeClass("video");
                                $("#hp-slideshow .rs-gallery-desc-more-link a span").text("Read More");
                            }

                            if(!$(".rs-gallery-desc-outer").length) {
                              $(".rs-gallery-desc").wrap("
").children().wrapAll('
');
                            }

                            $(".rs-gallery-bullets-outer").appendTo(".rs-gallery-container");

                            $(".rs-gallery-control.back, .rs-gallery-control.next").appendTo(".rs-gallery-images");
                        }
                });

                $("#hp-slideshow").prependTo(".hp.column.one.outer-column");
            }
          }


    	}
	}

    function mmgCollages() {
    	// FOR COLLAGE APPS
        $('#gb-page .ui-widget.app.multimedia-gallery-small').each(function() {
            var mmg = eval("multimediaGallery" + $('.ui-widget.app.multimedia-gallery-small').attr("data-pmi"));
            mmg.props.defaultGallery = false;

            $('.ui-widget.app.multimedia-gallery-small').csMultimediaGallery({
                "efficientLoad" : false,
                "imageWidth" : 590,
                "imageHeight" : 340,
                "mobileDescriptionContainer": [768, 640, 480, 320], // [960, 768, 640, 480, 320]
                "playPauseControl" : false,
                "backNextControls" : true,
                "autoRotate" : false,
                "hoverPause" : false,
                "onImageLoad" : function(props) {
                    var windowWidth = $(window).width();
                    if(windowWidth > 768) {
                        var smImageHeight = $(".mmg-slides:nth-child(2) .mmg-slide:first-child", props.element).height();
                        var smImageMargin = smImageHeight * .04504505;
                        var lgImageHeight = $(".mmg-slides:first-child .mmg-slide:first-child", props.element).height();
                        var lgImageMargin = lgImageHeight * .0295858;

                        $(".mmg-slides:nth-child(2) .mmg-slide:nth-child(-n+2)", props.element).css("margin-bottom",smImageMargin);
                        $(".mmg-slides:first-child .mmg-slide:first-child", props.element).css("margin-bottom",lgImageMargin);
                    }
                },
                "onTransitionStart" : function(props) { },// props.element, props.currentRecordIndex, props.currentGalleryIndex, props.nextRecordIndex, props.nextGalleryIndex, props.mmgRecords
                "allLoaded" : function(props) { // props.element, props.currentRecordIndex, props.currentGalleryIndex, props.mmgRecords

                    // SET TABINDEX TO 0
                    $(".mmg-slide", props.element).attr({"tabindex": 0, "aria-hidden": false});

                    var windowWidth = $(window).width();
                    if(windowWidth > 768) {
                        // SPLIT IMAGES INTO 2 COLUMNS
                        if(props.mmgRecords.length > 2) {
                            $(".mmg-slides-outer", props.element).append('');
                            $(".mmg-slide:nth-child(n+3)", props.element).appendTo($(".mmg-slides", props.element).eq(1));
                        }
                    }

                    // ADD IN ICONS FOR LINKS, ADD BACK TEXT FOR ACCESSIBILITY
                    $(".mmg-slide", props.element).each(function(index) {
                        if(!$(".mmg-description", this).length) {
                            $(this).addClass("cs-no-hover");
                        } else {
                            $(".mmg-description", this).wrapInner('
');
                        }
                    });
                    // IF SLIDEOR ANY CHILD ELMENT IS FOCUSED, KEEP FOCUS
                    $(".mmg-slide", props.element).on('focusin', function() {
                        $(this).addClass("focus");
                    });

                    $(".mmg-slide", props.element).on('focusout', function() {
                        var $elem = $(this);
                        setTimeout(function() {
                            var hasFocus = !! ($elem.find(':focus').length > 0);
                            if (! hasFocus) {
                                $elem.removeClass("focus");
                            }
                        }, 10);
                    });


                },
                "onWindowResize": function(props) {
                    var windowWidth = $(window).width();
                    if(windowWidth > 768) {
                        var smImageHeight = $(".mmg-slides:nth-child(2) .mmg-slide:first-child", props.element).height();
                        var smImageMargin = smImageHeight * .04504505;
                        var lgImageHeight = $(".mmg-slides:first-child .mmg-slide:first-child", props.element).height();
                        var lgImageMargin = lgImageHeight * .0295858;

                        $(".mmg-slides:nth-child(2) .mmg-slide:nth-child(-n+2)", props.element).css("margin-bottom",smImageMargin);
                        $(".mmg-slides:first-child .mmg-slide:first-child", props.element).css("margin-bottom",lgImageMargin);
                    }

                }
            });
        });

        $('#gb-page .ui-widget.app.multimedia-gallery-large').each(function() {
            var mmg = eval("multimediaGallery" + $('.ui-widget.app.multimedia-gallery-large').attr("data-pmi"));
            mmg.props.defaultGallery = false;

            $('.ui-widget.app.multimedia-gallery-large').csMultimediaGallery({
                "efficientLoad" : false,
                "imageWidth" : 630,
                "imageHeight" : 472,
                "mobileDescriptionContainer": [768, 640, 480, 320], // [960, 768, 640, 480, 320]
                "playPauseControl" : false,
                "backNextControls" : true,
                "autoRotate" : false,
                "hoverPause" : false,
                "onImageLoad" : function(props) {},
                "onTransitionStart" : function(props) { },// props.element, props.currentRecordIndex, props.currentGalleryIndex, props.nextRecordIndex, props.nextGalleryIndex, props.mmgRecords
                "allLoaded" : function(props) { // props.element, props.currentRecordIndex, props.currentGalleryIndex, props.mmgRecords

                    // SET TABINDEX TO 0
                    $(".mmg-slide", props.element).attr({"tabindex": 0, "aria-hidden": false});

                    var windowWidth = $(window).width();
                    if(windowWidth > 768) {
                        // SPLIT IMAGES INTO 2 COLUMNS
                        if(props.mmgRecords.length > 2) {
                            $(".mmg-slides-outer", props.element).append('');
                            $(".mmg-slide:nth-child(n+6)", props.element).appendTo($(".mmg-slides", props.element).eq(2));
                            $(".mmg-slide:nth-child(4)", props.element).appendTo($(".mmg-slides", props.element).eq(1));
                            $(".mmg-slide:nth-child(4)", props.element).appendTo($(".mmg-slides", props.element).eq(1));
                        }
                    }

                    // ADD IN ICONS FOR LINKS, ADD BACK TEXT FOR ACCESSIBILITY
                    $(".mmg-slide", props.element).each(function(index) {
                        if(!$(".mmg-description", this).length) {
                            $(this).addClass("cs-no-hover");
                        } else {
                            $(".mmg-description", this).wrapInner('
');
                        }
                    });
                    // IF SLIDEOR ANY CHILD ELMENT IS FOCUSED, KEEP FOCUS
                    $(".mmg-slide", props.element).on('focusin', function() {
                        $(this).addClass("focus");
                    });

                    $(".mmg-slide", props.element).on('focusout', function() {
                        var $elem = $(this);
                        setTimeout(function() {
                            var hasFocus = !! ($elem.find(':focus').length > 0);
                            if (! hasFocus) {
                                $elem.removeClass("focus");
                            }
                        }, 10);
                    });


                },
                "onWindowResize": function(props) {}
            });
        });
    }

    function checkSlideshow() {
      if($(".hp").length && csGetBreakPoint() != "desktop") {
			if ($(window).scrollTop() <= $("#hp-slideshow").offset().top + $("#hp-slideshow").height()) {
				if(csSlideshowDescFixed) {
					$("#hp-slideshow .rs-gallery-desc-outer").removeAttr("style");

					csSlideshowDescFixed = false;
				}
			} else {
				if(!csSlideshowDescFixed) {
					$("#hp-slideshow .rs-gallery-desc-outer").css({
						"height": $("#hp-slideshow .rs-gallery-desc-outer").height(),
						"overflow": "hidden"
					});

					csSlideshowDescFixed = true;
				}
			}
		}
    }

    function streamingVideo() {
        	if("false" == "true") {

                $('#sw-content-container10 > div[id*="pmi"]').hide();

            	var videoVendor = ("YouTube" == "YouTube;Vimeo") ? "youtube" : "YouTube";

                $("#sw-content-container10.region.ui-hp").fullScreenRotator({
                    "videoSource" : videoVendor, // OPTIONS ARE: youtube, vimeo
                    "videoID": "mxD7QEt85ms", // YouTube and Vimeo ids are set as default in the script
                    "fullScreenBreakpoints" : [], // OPTIONS ARE [960, 768, 640, 480, 320]
                    "showControls" : true,
                    "showProgressBar" : false, // CURRENTLY NOT IN USE
                    "progressBarType" : "line", // CURRENTLY NOT IN USE BUT OPTIONS ARE: line, circle
                    "progressBarCircleWidth": 70, // pixels
                    "progressBarCircleHeight": 70, // pixels
                    "progressBarStrokeWidth": 10, // pixels
                    "progressBarStrokeColor": "#FFF",
                    "showMobileBackgroundPhoto" : "false",
                    "mobileBackgroundPhoto" : "",
                    "mobileBackgroundPhotoBreakpoint" : 768, // OPTIONS ARE 768, 640, 480, 320
                    "useCustomResize": true,
                    "extendResize": function(element, videoHeight, videoWidth){
                    	var viewWidth = $(element).width();
                        var viewRatio = videoHeight / videoWidth;
                        var viewHeight = viewWidth * viewRatio;
                        var sliderWidth = viewWidth;

                        $('#cs-fullscreen-video-outer').css({"width": sliderWidth + "px", "height": viewHeight + "px"});
                        $('#cs-fullscreen-video').css({"width": viewWidth + "px", "height": viewHeight + "px"});
                    },
                    "onReady" : function() { 
                    	//add our custom controls
                    	$('#hp-slideshow .cs-fullscreen-video-buttons').append('');
                    	$('#hp-slideshow .cs-fullscreen-video-buttons').append('');

                    	$("#hp-slideshow .mmg-control.audio").click(function(e){
                        	if( $(this).hasClass('muted') ){
                            	$(this)
                                	.attr('aria-label', 'Mute streaming video')
                                	.removeClass('muted').addClass('unmuted');
                                $('.cs-fullscreen-video-button.unmute-button').click();
                            }else{
                            	$(this)
                                	.attr('aria-label', 'Unmute streaming video')
                                	.removeClass('unmuted').addClass('muted');
                                $('.cs-fullscreen-video-button.mute-button').click();
                            }
                        });

                        $("#hp-slideshow .mmg-control.play-pause").click(function(e){
                        	if( $(this).hasClass('playing') ){
                            	$(this)
                                	.attr('aria-label', 'Play streaming video')
                                	.removeClass('playing').addClass('paused');
                                $('.cs-fullscreen-video-button.pause-button').click();
                            }else{
                            	$(this)
                                	.attr('aria-label', 'Pause streaming video')
                                	.removeClass('paused').addClass('playing');
                                $('.cs-fullscreen-video-button.play-button').click();
                            }
                        });

                        $('#hp-slideshow .cs-fullscreen-video-buttons').appendTo("#cs-fullscreen-video-outer");


                        // add the description if there's info for it.
                        if('' != '' || '' != '' || '' != ''){                             
                        	var streamingDesc = '


';

                            streamingDesc += ('' != '') ? '

' : '';
                            streamingDesc += ('' != '') ? '

' : '';
                            streamingDesc += ('' != '') ? '
' : '';

                            streamingDesc += '';

                            $('#hp-slideshow .cs-fullscreen-video-buttons').after(streamingDesc);
                        }
                    },
                    "onStateChange" : function() { }
                });
            }
        }

        function tabbedApps() {
        	if($("#sw-content-container8.ui-hp .ui-widget.app").length >= 2) {
        		$("#sw-content-container8.ui-hp").tabBlocks();
            } else if($("#sw-content-container8.ui-hp .ui-widget.app").length == 1) {
            	$(".hp.column.tabbed-region").addClass("no-tabs");
            } else {
            	$(".hp.column.tabbed-region").remove();
            }
        }

        function tabbedSM() {
        	if($("#sw-content-container9.ui-hp .ui-widget.app").length >= 2) {
        		$("#sw-content-container9.ui-hp").tabBlocks();
            } else if($("#sw-content-container9.ui-hp .ui-widget.app").length == 1) {
            	$(".social-media-tabs").addClass("no-tabs");
                $("#sw-content-container9.ui-hp .ui-widget.app").each(function(){
                    if($(".ui-widget-header h1:contains('Facebook')",this)){
                        $(".ui-widget-header",this).addClass("facebook").html('');
                    } else if($(".ui-widget-header h1:contains('Twitter')",this)){
                        $(".ui-widget-header",this).addClass("twitter").html('');
                    } else if($(".ui-widget-header h1:contains('Instagram')",this)){
                        $(".ui-widget-header",this).addClass("instagram").html('');
                    }
                });
            } else {
            	$(".social-media-tabs").remove();
            }

            $("#sw-content-container9.ui-hp .tabs-container .tab-button:eq(0)").addClass('facebook show-true').attr("data-show","true");
            $("#sw-content-container9.ui-hp .tabs-container .tab-button:eq(0) .tab-right").html('');
            $("#sw-content-container9.ui-hp .tabs-container .tab-button:eq(1)").addClass('twitter show-true').attr("data-show","true");
            $("#sw-content-container9.ui-hp .tabs-container .tab-button:eq(1) .tab-right").html('');
            $("#sw-content-container9.ui-hp .tabs-container .tab-button:eq(2)").addClass('instagram show-true').attr("data-show","true");
            $("#sw-content-container9.ui-hp .tabs-container .tab-button:eq(2) .tab-right").html('');


        }

